import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';

const Dashboard = () => {
  const { players, currentPlayerIndex } = useContext(GameContext);
  const me = players[currentPlayerIndex];
  if (!me) return null;

  return (
    <div style={{
      position: 'absolute', bottom: 10, left: '50%',
      transform: 'translateX(-50%)', background: '#f0f0f0',
      padding: '10px 20px', borderRadius: 8, border: '1px solid #ccc'
    }}>
      <h3>{me.name}'s Dashboard</h3>
      <p><strong>Money:</strong> ${me.money || 0}</p>
      <p><strong>Properties:</strong> {(me.properties || []).join(', ') || 'None'}</p>
    </div>
  );
};

export default Dashboard;
