// src/components/Lobby.js
import React, { useContext, useState, useEffect } from 'react';
import { GameContext } from '../context/GameContext';
import io from 'socket.io-client';

const socket = io('http://localhost:3001');

const allPieces = [
  'piece1.png', 'piece2.png', 'piece3.png', 'piece4.png',
  'piece5.png', 'piece6.png', 'piece7.png', 'piece8.png'
];

const Lobby = () => {
  const {
    player,
    setPlayer,
    players = [],
    setPlayers,
    setGameState
  } = useContext(GameContext);

  const [name, setName] = useState('');
  const [hasJoined, setHasJoined] = useState(false);
  const [selectedPiece, setSelectedPiece] = useState('');
  const [readyPlayers, setReadyPlayers] = useState([]);

  useEffect(() => {
    socket.on('lobbyUpdate', updated => setPlayers(updated || []));
    socket.on('readyUpdate', updated => setReadyPlayers(updated || []));
    socket.on('gameStart', () => setGameState('playing'));
    return () => {
      socket.off('lobbyUpdate');
      socket.off('readyUpdate');
      socket.off('gameStart');
    };
  }, [setPlayers, setGameState]);

  const joinLobby = () => {
    if (!name.trim()) return;
    const newPlayer = {
      name,
      piece: null,
      ready: false,
      tileId: 1,
      prevTile: null
    };
    setPlayer(newPlayer);
    setHasJoined(true);
    socket.emit('joinLobby', newPlayer);
  };

  const choosePiece = piece => {
    const updated = { ...player, piece };
    setSelectedPiece(piece);
    setPlayer(updated);
    socket.emit('selectPiece', { name: player.name, piece });
  };

  const handleReady = () => {
    socket.emit('playerReady', player.name);
  };

  const used = players.map(p => p.piece).filter(Boolean);
  const available = allPieces.filter(p => !used.includes(p));

  const allReady = players.length >= 2 &&
                   players.every(p => p.piece && readyPlayers.includes(p.name));

  return (
    <div style={{
      display: 'flex', flexDirection: 'column',
      alignItems: 'center', justifyContent: 'center',
      height: '100vh', fontSize: '1.2rem'
    }}>
      <h2>Business Lobby</h2>

      {!hasJoined ? (
        <>
          <input
            placeholder="Enter your name"
            value={name}
            onChange={e => setName(e.target.value)}
            style={{ marginBottom: '1rem', fontSize: '1.2rem' }}
          />
          <button onClick={joinLobby}>Join Lobby</button>
        </>
      ) : !selectedPiece ? (
        <>
          <p>Select your game piece:</p>
          <div style={{
            display: 'flex', flexWrap: 'wrap', gap: '10px',
            justifyContent: 'center'
          }}>
            {available.map((piece, i) => (
              <div key={i} style={{ textAlign: 'center' }}>
                <img
                  src={`/pieces/${piece}`}
                  alt={piece}
                  style={{ width: 60, height: 60, cursor: 'pointer' }}
                  onClick={() => choosePiece(piece)}
                />
                <div>{piece.replace('.png', '')}</div>
              </div>
            ))}
          </div>
        </>
      ) : (
        <>
          <p>
            Welcome, <strong>{player.name}</strong>! Youâ€™ve selected:&nbsp;
            <img src={`/pieces/${selectedPiece}`} alt="" style={{ width: 30 }} />
          </p>
          {allReady && <button onClick={handleReady}>Start Game</button>}
        </>
      )}

      <hr style={{ width: '60%', margin: '2rem 0' }} />

      <h3>Players in Lobby:</h3>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {players.map((p, i) => (
          <li key={i}>
            {p.name}{' '}
            {p.piece && (
              <img
                src={`/pieces/${p.piece}`}
                alt={p.piece}
                style={{ width: 20, verticalAlign: 'middle' }}
              />
            )}{' '}
            {readyPlayers.includes(p.name) && 'âœ…'}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Lobby;
