// src/components/Board.js
import React, { useContext, useEffect, useRef, useState } from 'react';
import { GameContext } from '../context/GameContext';
import boardImg from '../assets/board.png';

const tilePositions = {
  1: { x: 100, y: 100 }, // Replace with actual tile centers
  // Add more tile IDs and positions here...
};

const Board = () => {
  const canvasRef = useRef(null);
  const {
    players
  } = useContext(GameContext);

  const [scale, setScale] = useState(1);
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const board = new Image();
    board.src = boardImg;

    board.onload = () => {
      canvas.width = board.width;
      canvas.height = board.height;
      draw(ctx, board);
    };
  }, [players, scale]);

  const draw = (ctx, board) => {
    ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height);
    ctx.save();
    ctx.scale(scale, scale);
    ctx.drawImage(board, 0, 0);

    players.forEach(player => {
      if (player.piece) {
        const img = new Image();
        img.src = require(`../assets/pieces/${player.piece}`);
        const tile = tilePositions[player.tileId || 1];
        img.onload = () => {
          ctx.drawImage(img, tile.x, tile.y, 30, 30);
        };
      }
    });

    ctx.restore();
  };

  const handleWheel = (e) => {
    e.preventDefault();
    const delta = e.deltaY < 0 ? 0.1 : -0.1;
    setScale(prev => Math.min(Math.max(prev + delta, 0.5), 2));
  };

  const handleMouseMove = (e) => {
    const rect = canvasRef.current.getBoundingClientRect();
    const x = Math.floor((e.clientX - rect.left) / scale);
    const y = Math.floor((e.clientY - rect.top) / scale);
    setMousePos({ x, y });
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <div style={{ marginBottom: 8 }}>Coordinates: {mousePos.x}, {mousePos.y}</div>
      <canvas
        ref={canvasRef}
        onWheel={handleWheel}
        onMouseMove={handleMouseMove}
        style={{ border: '2px solid black', cursor: 'crosshair' }}
      />
    </div>
  );
};

export default Board;
