import React, { createContext, useState, useEffect } from 'react';
import socket from '../socket';

export const GameContext = createContext();

export function GameProvider({ children }) {
  const [player, setPlayer] = useState(null);
  const [players, setPlayers] = useState([]);
  const [currentPlayerId, setCurrentPlayerId] = useState(null);
  const [sessionId, setSessionId] = useState(null);
  const [gameState, setGameState] = useState('lobby');

  // NEW: diceRoll holds { playerId, die1, die2 } or null
  const [diceRoll, setDiceRoll] = useState(null);
  // NEW: movementDone flag to enable the "Done" button
  const [movementDone, setMovementDone] = useState(false);

  useEffect(() => {
    // Lobby update
    socket.on('lobbyUpdate', updated => {
      setPlayers(updated);
      const me = updated.find(p => p.socketId === socket.id);
      if (me) setPlayer(me);
    });

    // Game start
    socket.on('gameStart', ({ players: ps, sessionId: sid, currentPlayerId }) => {
      setPlayers(ps);
      setSessionId(sid);
      setGameState('playing');
      setCurrentPlayerId(currentPlayerId);
      setDiceRoll(null);
      setMovementDone(false);
      console.log('[GameContext] gameStart → currentPlayerId:', currentPlayerId);
    });

    // Turn ended
    socket.on('turnEnded', ({ nextPlayerId }) => {
      setCurrentPlayerId(nextPlayerId);
      setDiceRoll(null);
      setMovementDone(false);
      console.log('[GameContext] turnEnded → nextPlayerId:', nextPlayerId);
    });

    // Dice result from server
    socket.on('diceResult', ({ playerId, die1, die2 }) => {
      console.log('[GameContext] diceResult', { playerId, die1, die2 });
      setDiceRoll({ playerId, die1, die2 });
    });

    // Per‐tile move updates: now also capture prevTile
    socket.on('playerMoved', ({ playerId, tileId }) => {
      setPlayers(prev =>
        prev.map(p => {
          if (p.socketId !== playerId) return p;
          return {
            ...p,
            prevTile: p.tileId,  // ← store the old position
            tileId               // ← update to the new position
          };
        })
      );
      console.log('[GameContext] playerMoved', { playerId, tileId });
    });

    // Movement complete: show Done button
    socket.on('movementDone', () => {
      console.log('[GameContext] movementDone');
      setMovementDone(true);
    });

    return () => {
      socket.off('lobbyUpdate');
      socket.off('gameStart');
      socket.off('turnEnded');
      socket.off('diceResult');
      socket.off('playerMoved');
      socket.off('movementDone');
    };
  }, []);

  return (
    <GameContext.Provider
      value={{
        player,
        setPlayer,
        players,
        setPlayers,
        currentPlayerId,
        setCurrentPlayerId,
        sessionId,
        setSessionId,
        socket,
        gameState,
        setGameState,
        diceRoll,
        setDiceRoll,
        movementDone,
        setMovementDone,
      }}
    >
      {children}
    </GameContext.Provider>
  );
}
