import React, { useContext, useState } from 'react';
import { GameContext } from '../context/GameContext';

const DiceRoller = () => {
  const {
    player,
    players,
    currentPlayerIndex,
    setOriginalCount,
    isMoving,
    setIsMoving,
    setCurrentPlayerIndex
  } = useContext(GameContext);

  const [diceRoll, setDiceRoll] = useState(null);
  const isMyTurn = players[currentPlayerIndex]?.name === player?.name;

  const rollDice = () => {
    if (!isMyTurn || isMoving) return;
    const d1 = Math.ceil(Math.random() * 6);
    const d2 = Math.ceil(Math.random() * 6);
    setDiceRoll([d1, d2]);
    setOriginalCount(d1 + d2);
    setIsMoving(true);
  };

  const finishTurn = () => {
    setIsMoving(false);
    setDiceRoll(null);
    setCurrentPlayerIndex((currentPlayerIndex + 1) % players.length);
  };

  if (!isMyTurn) return null;

  return (
    <div style={{
      position: 'absolute',
      top: 20,
      left: '50%',
      transform: 'translateX(-50%)',
      background: '#fff',
      padding: 10,
      border: '1px solid #444',
      borderRadius: 8,
      zIndex: 1000
    }}>
      {!diceRoll && <button onClick={rollDice}>Roll Dice</button>}

      {diceRoll && (
        <div style={{ textAlign: 'center' }}>
          <div style={{ margin: '10px 0' }}>
            <img src={`/dice/dice${diceRoll[0]}.png`} alt="die1" width={50} />
            <img src={`/dice/dice${diceRoll[1]}.png`} alt="die2" width={50} />
          </div>
          {!isMoving && <button onClick={finishTurn}>Done</button>}
        </div>
      )}
    </div>
  );
};

export default DiceRoller;
