// src/context/GameContext.js
import React, { createContext, useState } from 'react';

export const GameContext = createContext();

export const GameProvider = ({ children }) => {
  const [player, setPlayer] = useState(null);
  const [players, setPlayers] = useState([]);
  const [gameState, setGameState] = useState('lobby');
  const [currentPlayerIndex, setCurrentPlayerIndex] = useState(0);
  const [originalCount, setOriginalCount] = useState(0);
  const [isMoving, setIsMoving] = useState(false);
  const [diceRoll, setDiceRoll] = useState(null);

  // NEW:
  const [pendingPaths, setPendingPaths] = useState(null);
  const choosePath = (to) => {
    setPendingPaths(null);
    // stash the choice for the controller to pick up:
    setPendingPaths({ chosen: to });
  };

  return (
    <GameContext.Provider value={{
      player, setPlayer,
      players, setPlayers,
      gameState, setGameState,
      currentPlayerIndex, setCurrentPlayerIndex,
      originalCount, setOriginalCount,
      isMoving, setIsMoving,
      diceRoll, setDiceRoll,
      pendingPaths, choosePath,
    }}>
      {children}
    </GameContext.Provider>
  );
};
