import React, { useState, useEffect } from 'react';
import cash500 from '../assets/cash500.png';
import cash1000 from '../assets/cash1000.png';
import cash5000 from '../assets/cash5000.png';
import cash10000 from '../assets/cash10000.png';
import cashBehind from '../assets/cashBehind.png';

const cashImages = [
  { amount: 500, image: cash500 },
  { amount: 1000, image: cash1000 },
  { amount: 5000, image: cash5000 },
  { amount: 10000, image: cash10000 }
];

const RoadCash = ({ isMyTurn, socket }) => {
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [revealedAmount, setRevealedAmount] = useState(null);
  const [showAll, setShowAll] = useState(false);
  const [isActive, setIsActive] = useState(true);

  const handleCashClick = (index) => {
    if (!isMyTurn || selectedIndex !== null) return;

    setSelectedIndex(index);
    const randomAmount = cashImages[Math.floor(Math.random() * cashImages.length)].amount;
    setRevealedAmount(randomAmount);
    socket.emit('roadCashSelected', { amount: randomAmount });

    // First timer: Hide selected cash after 3 seconds
    setTimeout(() => {
      setShowAll(true);
      // Second timer: Show all remaining cash and hide after 3 more seconds
      setTimeout(() => {
        setIsActive(false);
      }, 3000);
    }, 3000);
  };

  if (!isActive) return null;

  return (
    <div style={{
      position: 'absolute',
      top: '50%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      display: 'grid',
      gridTemplateColumns: 'repeat(2, 1fr)',
      gap: '20px',
      padding: '20px',
      zIndex: 1000
    }}>
      {[0, 1, 2, 3].map((index) => {
        const isSelected = index === selectedIndex;
        const shouldShow = !isSelected || showAll;

        if (!shouldShow) return null;

        return (
          <img
            key={index}
            src={isSelected || showAll ? 
              cashImages.find(c => c.amount === (isSelected ? revealedAmount : cashImages[index].amount)).image 
              : cashBehind}
            alt={`Cash ${index + 1}`}
            onClick={() => handleCashClick(index)}
            style={{
              cursor: isMyTurn && !selectedIndex ? 'pointer' : 'default',
              transition: 'transform 0.3s ease',
              transform: isSelected ? 'scale(1.1)' : 'scale(1)',
              opacity: showAll ? 0.8 : 1
            }}
          />
        );
      })}
    </div>
  );
};

export default RoadCash;
