import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';
import io from 'socket.io-client';

const socket = io('http://localhost:3001');

const Dashboard = () => {
  const {
    players, setCurrentPlayerIndex,
    currentPlayerIndex, isMoving
  } = useContext(GameContext);

  const player = players[currentPlayerIndex];

  if (!player) return null;

  const handleExit = () => {
    socket.emit('exitLobby');
  };

  const handleDone = () => {
    setCurrentPlayerIndex((prev) => (prev + 1) % players.length);
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', marginTop: '1.5rem', fontSize: '1.1rem' }}>
      <div style={{
        border: '2px solid #444',
        borderRadius: '10px',
        padding: '1.2rem 2rem',
        width: '80%',
        maxWidth: '700px',
        backgroundColor: '#ffffff',
        boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
        textAlign: 'center'
      }}>
        <h3 style={{ marginBottom: '1rem' }}>{player.name}'s Dashboard</h3>
        <p><strong>Money:</strong> ${player.money}</p>
        <p><strong>Properties:</strong> {player.properties?.length ? player.properties.join(', ') : 'None'}</p>
        <div style={{ marginTop: '1rem' }}>
          <button onClick={handleExit}>Exit Game</button>
          {!isMoving && (
            <button onClick={handleDone} style={{ marginLeft: '1rem' }}>Done</button>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
