import React, { useContext } from 'react';
import io from 'socket.io-client';
import { GameContext } from '../context/GameContext';

const socket = io('http://localhost:3001');

const Dashboard = () => {
  const {
    players = [],
    currentPlayerIndex = 0,
    isMoving,
    setCurrentPlayerIndex
  } = useContext(GameContext);

  const player = players[currentPlayerIndex];
  if (!player) return null;

  const handleExit = () => {
    socket.emit('exitLobby');
  };

  const handleDone = () => {
    // advance to next player
    setCurrentPlayerIndex((prev) => (prev + 1) % players.length);
  };

  return (
    <div style={{
      display: 'flex',
      justifyContent: 'center',
      marginTop: '1.5rem',
      fontSize: '1.1rem'
    }}>
      <div style={{
        border: '2px solid #444',
        borderRadius: '10px',
        padding: '1.2rem 2rem',
        width: '80%',
        maxWidth: '700px',
        backgroundColor: '#ffffff',
        boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
        textAlign: 'center'
      }}>
        <h3 style={{ marginBottom: '1rem' }}>{player.name}'s Dashboard</h3>
        <p><strong>Money:</strong> ${typeof player.money === 'number' ? player.money : 0}</p>
        <p>
          <strong>Properties:</strong>{' '}
          {Array.isArray(player.properties) && player.properties.length > 0
            ? player.properties.join(', ')
            : 'None'}
        </p>
        <div style={{ marginTop: '1rem' }}>
          <button onClick={handleExit}>Exit Game</button>
          {/* only show Done once movement is finished and if there's more than one player */}
          {!isMoving && players.length > 1 && (
            <button onClick={handleDone} style={{ marginLeft: '1rem' }}>
              Done
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
