import React, { useContext, useState, useRef, useEffect } from 'react';
import { GameContext } from '../context/GameContext';
import boardImage from '../assets/board.png';

import piece1 from '../assets/pieces/piece1.png';
import piece2 from '../assets/pieces/piece2.png';
import piece3 from '../assets/pieces/piece3.png';
import piece4 from '../assets/pieces/piece4.png';
import piece5 from '../assets/pieces/piece5.png';
import piece6 from '../assets/pieces/piece6.png';
import piece7 from '../assets/pieces/piece7.png';
import piece8 from '../assets/pieces/piece8.png';

const pieceMap = {
  piece1, piece2, piece3, piece4, piece5, piece6, piece7, piece8,
};

const Board = () => {
  const { players } = useContext(GameContext);
  const [zoom, setZoom] = useState(1);
  const [coords, setCoords] = useState({ x: null, y: null });
  const [naturalSize, setNaturalSize] = useState({ width: 600, height: 600 });

  const imageRef = useRef();

  useEffect(() => {
    const img = new Image();
    img.onload = () => {
      setNaturalSize({ width: img.width, height: img.height });
    };
    img.src = boardImage;
  }, []);

  const zoomIn = () => setZoom((z) => Math.min(z + 0.1, 2));
  const zoomOut = () => setZoom((z) => Math.max(z - 0.1, 0.5));

  const handleClick = (e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const rawX = (e.clientX - rect.left) / zoom;
    const rawY = (e.clientY - rect.top) / zoom;

    const logicalX = Math.round(rawX);
    const logicalY = Math.round(rawY);

    console.log(`Tile Coordinates: (${logicalX}, ${logicalY})`);
    setCoords({ x: logicalX, y: logicalY });
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '20px' }}>
      <div style={{ marginBottom: '10px' }}>
        <button onClick={zoomOut} style={{ marginRight: '10px' }}>➖ Zoom Out</button>
        <button onClick={zoomIn}>➕ Zoom In</button>
      </div>

      <div
        onClick={handleClick}
        style={{
          position: 'relative',
          transform: `scale(${zoom})`,
          transformOrigin: 'top left',
          width: `${naturalSize.width}px`,
          height: `${naturalSize.height}px`,
          margin: '0 auto',
        }}
      >
        <img
          ref={imageRef}
          src={boardImage}
          alt="Game Board"
          style={{
            width: '100%',
            height: '100%',
            display: 'block',
          }}
        />

        {players.map((p, i) => {
          const pieceImg = pieceMap[p.piece];
          if (!pieceImg) return null;

          return (
            <img
              key={i}
              src={pieceImg}
              alt={p.name}
              title={p.name}
              style={{
                position: 'absolute',
                top: 520 + i * 10,
                left: 20 + i * 10,
                width: 30,
                height: 30,
                transition: 'top 0.3s, left 0.3s',
              }}
            />
          );
        })}

        {coords.x !== null && (
          <div
            style={{
              position: 'absolute',
              top: 10,
              left: 10,
              backgroundColor: 'rgba(255, 255, 255, 0.9)',
              padding: '4px 8px',
              fontSize: '14px',
              border: '1px solid #ccc',
              borderRadius: '4px',
            }}
          >
            Tile: ({coords.x}, {coords.y})
          </div>
        )}
      </div>
    </div>
  );
};

export default Board;
