          {panelId === 'chat' && (
            <div>
              <h3>Game Chat</h3>
              <p>Chat feature coming soon!</p>
              <p>Communicate with other players</p>
              <p>Share strategies and have fun!</p>
            </div>
          )}
          {panelId === 'trade' && (
            <div>
              <h3>Trade Center</h3>
              <p>â€¢ Propose trades with other players</p>
              <p>â€¢ Exchange properties</p>
              <p>â€¢ Negotiate deals</p>
              <p>â€¢ View trade history</p>
            </div>
          )}
        </div>
      ))}

      {/* Main content */}
      <div style={{
        display: 'flex',
        flexDirection: 'column',
        minHeight: '100vh',
        minWidth: '1200px',
        position: 'relative',
        backgroundImage: `url(${bgImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        overflow: 'auto'
      }}>
        {/* Main content (board & player stats) */}
        <div style={{
          flex: 1,
          display: 'flex',
          flexDirection: 'row',
          alignItems: 'center',
          justifyContent: 'center',
          position: 'relative',
          minWidth: '1080px',
          padding: '20px',
          marginTop: '220px',
          marginBottom: '20px'
        }}>
          {/* Board with centered positioning */}
          <div style={{ 
            position: 'relative',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
          width: '100%',
            height: '100%',
            minHeight: '600px' // Ensure minimum height for the board container
          }}>
            <div style={{ 
              position: 'relative',
              margin: '200px' // Add margin around the board to make space for player stats
            }}>
              <Board />
              <PlayerStats />
            </div>
          </div>
        </div>

        {/* Bottom sections (not fixed anymore) */}
        <div style={{
          width: '60%',
          minWidth: '800px',
          height: '240px',
          minHeight: '400px',
          background: 'rgba(80, 80, 80, 0.9)',
          borderTop: '2px solid #bbb',
          boxShadow: '0 -2px 10px rgba(0, 0, 0, 0.2)',
          zIndex: 100,
          display: 'flex',
          border: '2px solid #666',
          borderRadius: '30px 30px 0 0',
          margin: '80px auto 0 auto'
        }}>
          {/* Dice Roller Section */}
          <div style={{
            flex: 1,
            position: 'relative',
            borderRight: '2px solid #666',
            padding: '10px',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            background: 'rgba(60, 60, 60, 0.3)',
            borderRadius: '30px 30px 30px 30px',
            overflow: 'hidden'
          }}>
          {isMyTurn && (
              <>
            <img
              src={Dicebox}
              alt="Dice Board"
              style={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                objectFit: 'fill',
                    objectPosition: 'center',
                pointerEvents: 'none'
              }}
            />
                {testRollMode && (
                  <div style={{
                    position: 'absolute',
                    top: '50%',
                    left: '50%',
                    transform: 'translate(-50%, -50%)',
                    backgroundColor: 'rgba(0, 0, 0, 0.8)',
                    color: '#fff',
                    padding: '8px 16px',
                    borderRadius: '4px',
                    zIndex: 100
                  }}>
                    {testRollInput}
                  </div>
                )}
              </>
            )}
            <DiceRoller 
              testRollMode={testRollMode} 
              hasCasinoPlayed={hasCasinoPlayed}
              style={{ position: 'relative', zIndex: 1 }}
            />
          </div>

          {/* Dashboard Section */}
          <div style={{
            flex: 1,
            borderRight: '2px solid #666',
            padding: '10px',
            display: 'flex',
            flexDirection: 'column',
            background: 'rgba(60, 60, 60, 0.3)'
          }}>
            <Dashboard />
        </div>

          {/* Events Section */}
          <div style={{
            flex: 1,
            padding: '10px',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            background: 'rgba(60, 60, 60, 0.3)',
            borderRadius: '0 10px 0 0'
          }}>
          {(() => {
              // Show "Your Turn" for current player before landing
              if (isMyTurn && !movementDone) {
                return (
                  <div style={{
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    height: '100%',
                    color: '#fff',
                    fontSize: '1.6em',
                    fontWeight: 'bold',
                    textShadow: '2px 2px 4px rgba(0,0,0,0.5)'
                  }}>
                    Your Turn
                  </div>
                );
              }

              // Buy Property
              if (isMyTurn && tileMeta?.type === 'property' && 
                  !players.some(p => p.properties.includes(tileMeta?.id))) {
                return (
                  <div style={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    gap: '10px'
                  }}>
                <button
                  onClick={handleBuy}
                  disabled={player.money < tileMeta.cost}
                      style={{
                        padding: '12px 24px',
                        fontSize: '1.1em',
                        backgroundColor: player.money >= tileMeta.cost ? '#4CAF50' : '#ccc',
                        color: player.money >= tileMeta.cost ? 'white' : '#ff0000',
                        border: 'none',
                        borderRadius: '8px',
                        cursor: player.money >= tileMeta.cost ? 'pointer' : 'not-allowed',
                        fontWeight: 'bold'
                      }}
                >
                  Buy (${tileMeta.cost})
                </button>
                {error && (
                      <p style={{ color: 'tomato', margin: 0 }}>{error}</p>
                )}
        </div>
                );
              }

              // Road Cash Game
              if (isMyTurn && tileMeta?.id === 22) {
                return <RoadCash isMyTurn={isMyTurn} socket={socket} />;
              }

              // Casino
              if (inCasino && isMyTurn) {
                return (
                  <CasinoBetting 
                    isMyTurn={isMyTurn} 
                    currentMoney={player?.money || 0}
                    socket={socket}
                    player={player}
                    onCasinoPlayed={() => setHasCasinoPlayed(true)}
                  />
                );
              }

              // Corner Choice
              if (isMyTurn && tileMeta?.name?.toLowerCase().includes('choose corner')) {
                return (
                  <div style={{
                    display: 'grid',
                    gridTemplateColumns: '1fr 1fr',
                    gridTemplateRows: '1fr 1fr',
                    gap: '20px',
                    width: '100%',
                    maxWidth: '600px',
                    padding: '20px',
                    margin: '0 auto'
                  }}>
                    <button
                      onClick={() => {
                        socket.emit('teleport', { toTile: 1, prevTile: 30 });
                        setError(null);
                        setHasChosenCorner(true);
                      }}
                      style={{
                        margin: 0,
                        padding: 0,
                        border: '3px solid #666',
                        borderRadius: '12px',
                        backgroundImage: `url(${startIcon})`,
                        backgroundSize: '80%',
                        backgroundRepeat: 'no-repeat',
                        backgroundPosition: 'center 40%',
                        cursor: 'pointer',
                        display: 'flex',
                        alignItems: 'flex-end',
                        justifyContent: 'center',
                        width: '100%',
                        height: '100%',
                        minHeight: '180px',
                        position: 'relative',
                        backgroundColor: 'rgba(255, 255, 255, 0.15)',
                        transition: 'transform 0.2s, background-color 0.2s',
                        ':hover': {
                          transform: 'scale(1.02)',
                          backgroundColor: 'rgba(255, 255, 255, 0.2)'
                        }
                      }}
                    >
                      <span style={{
                        padding: '8px 16px',
                        backgroundColor: 'rgba(0,0,0,0.6)',
                        borderRadius: '0 0 9px 9px',
                        color: '#fff',
                        fontWeight: 'bold',
                        fontSize: '1.4em',
                        textShadow: '2px 2px 4px rgba(0,0,0,0.7)',
                        position: 'absolute',
                        bottom: 0,
                        width: '100%',
                        textAlign: 'center'
                      }}>Start</span>
                    </button>
                    <button
                      onClick={() => {
                        socket.emit('teleport', { toTile: 22, prevTile: 21 });
                        setError(null);
                        setHasChosenCorner(true);
                      }}
                      style={{
                        margin: 0,
                        padding: 0,
                        border: '3px solid #666',
                        borderRadius: '12px',
                        backgroundImage: `url(${roadIcon})`,
                        backgroundSize: '80%',
                        backgroundRepeat: 'no-repeat',
                        backgroundPosition: 'center 40%',
                        cursor: 'pointer',
                        display: 'flex',
                        alignItems: 'flex-end',
                        justifyContent: 'center',
                        width: '100%',
                        height: '100%',
                        minHeight: '180px',
                        position: 'relative',
                        backgroundColor: 'rgba(255, 255, 255, 0.15)',
                        transition: 'transform 0.2s, background-color 0.2s',
                        ':hover': {
                          transform: 'scale(1.02)',
                          backgroundColor: 'rgba(255, 255, 255, 0.2)'
                        }
                      }}
                    >
                      <span style={{
                        padding: '8px 16px',
                        backgroundColor: 'rgba(0,0,0,0.6)',
                        borderRadius: '0 0 9px 9px',
                        color: '#fff',
                        fontWeight: 'bold',
                        fontSize: '1.4em',
                        textShadow: '2px 2px 4px rgba(0,0,0,0.7)',
                        position: 'absolute',
                        bottom: 0,
                        width: '100%',
                        textAlign: 'center'
                      }}>Road</span>
                    </button>
                    <button
                      onClick={() => {
                        socket.emit('teleport', { toTile: 7, prevTile: 6 });
                        setError(null);
                        setHasChosenCorner(true);
                      }}
        style={{
                        margin: 0,
                        padding: 0,
                        border: '3px solid #666',
                        borderRadius: '12px',
                        backgroundImage: `url(${hotelIcon})`,
                        backgroundSize: '80%',
                        backgroundRepeat: 'no-repeat',
                        backgroundPosition: 'center 40%',
                        cursor: 'pointer',
          display: 'flex',
                        alignItems: 'flex-end',
          justifyContent: 'center',
                        width: '100%',
                        height: '100%',
                        minHeight: '180px',
                        position: 'relative',
                        backgroundColor: 'rgba(255, 255, 255, 0.15)',
                        transition: 'transform 0.2s, background-color 0.2s',
                        ':hover': {
                          transform: 'scale(1.02)',
                          backgroundColor: 'rgba(255, 255, 255, 0.2)'
                        }
                      }}
                    >
                      <span style={{
                        padding: '8px 16px',
                        backgroundColor: 'rgba(0,0,0,0.6)',
                        borderRadius: '0 0 9px 9px',
                        color: '#fff',
                        fontWeight: 'bold',
                        fontSize: '1.4em',
                        textShadow: '2px 2px 4px rgba(0,0,0,0.7)',
                        position: 'absolute',
                        bottom: 0,
                        width: '100%',
                        textAlign: 'center'
                      }}>Hotel</span>
                    </button>
                    <button
                      onClick={() => {
                        socket.emit('teleport', { toTile: 16, prevTile: 15 });
                        setError(null);
                        setHasChosenCorner(true);
                      }}
          style={{
                        margin: 0,
                        padding: 0,
                        border: '3px solid #666',
                        borderRadius: '12px',
                        backgroundImage: `url(${casinoIcon})`,
                        backgroundSize: '80%',
                        backgroundRepeat: 'no-repeat',
                        backgroundPosition: 'center 40%',
                        cursor: 'pointer',
            display: 'flex',
                        alignItems: 'flex-end',
                        justifyContent: 'center',
                        width: '100%',
                        height: '100%',
                        minHeight: '180px',
                        position: 'relative',
                        backgroundColor: 'rgba(255, 255, 255, 0.15)',
                        transition: 'transform 0.2s, background-color 0.2s',
                        ':hover': {
                          transform: 'scale(1.02)',
                          backgroundColor: 'rgba(255, 255, 255, 0.2)'
                        }
                      }}
                    >
                      <span style={{
                        padding: '8px 16px',
                        backgroundColor: 'rgba(0,0,0,0.6)',
                        borderRadius: '0 0 9px 9px',
                        color: '#fff',
                        fontWeight: 'bold',
                        fontSize: '1.4em',
                        textShadow: '2px 2px 4px rgba(0,0,0,0.7)',
                        position: 'absolute',
                        bottom: 0,
                        width: '100%',
                        textAlign: 'center'
                      }}>Casino</span>
                    </button>
        </div>
                );
              }

              // Show error message if any
              if (error) {
                return (
                  <p style={{ color: 'tomato', margin: 0 }}>{error}</p>
                );
              }

              // Show empty state for non-turn players or when no action is needed
              return (
                <div style={{
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  justifyContent: 'center',
                  height: '100%',
                  gap: '20px'
                }}>
                  {rpsGame && (
                    <>
                      {rpsResult ? (
                        // Show result
                        <div style={{
                          textAlign: 'center',
                          color: '#fff',
                          fontSize: '1.4em'
                        }}>
                          <div>
                            {rpsResult.landingPlayer.name}: {rpsResult.landingPlayerChoice}
                          </div>
                          <div>
                            {rpsResult.closestPlayer.name}: {rpsResult.closestPlayerChoice}
                          </div>
                          <div style={{ marginTop: '10px', color: '#4CAF50' }}>
                            {rpsResult.landingPlayerWon ? rpsResult.landingPlayer.name : rpsResult.closestPlayer.name} won!
        </div>
      </div>
                      ) : rpsTieAmount ? (
                        <RPSTieResolver
                          maxAmount={rpsTieAmount.maxAmount}
                          gameId={rpsTieAmount.gameId}
                          socket={socket}
                          onResolved={() => {
                            setRpsTieAmount(null);
                            setRpsGame(null);
                          }}
                        />
                      ) : (
                        // Show RPS buttons
                        <div style={{
                          display: 'flex',
                          flexDirection: 'column',
                          alignItems: 'center',
                          gap: '15px'
                        }}>
                          {(player?.socketId === rpsGame.landingPlayer.socketId ||
                           player?.socketId === rpsGame.closestPlayer.socketId) && !rpsChoice && (
                            <>
                              <div style={{ color: '#fff', fontSize: '1.2em' }}>
                                Choose your move:
                              </div>
                              <div style={{
                                display: 'flex',
                                gap: '10px'
                              }}>
                                {['rock', 'paper', 'scissors'].map(choice => (
                                  <button
                                    key={choice}
                                    onClick={() => {
                                      setRpsChoice(choice);
                                      socket.emit('stonePaperScissorsChoice', {
                                        choice,
                                        gameId: rpsGame.gameId
                                      });
                                    }}
        style={{
                                      padding: '12px 24px',
                                      fontSize: '1.1em',
                                      backgroundColor: '#2196F3',
                                      color: 'white',
                                      border: 'none',
                                      borderRadius: '4px',
                                      cursor: 'pointer',
                                      textTransform: 'capitalize'
                                    }}
                                  >
                                    {choice}
                                  </button>
                                ))}
                              </div>
                            </>
                          )}
                          {rpsChoice && (
                            <div style={{ color: '#fff', fontSize: '1.2em' }}>
                              You chose: {rpsChoice}
                            </div>
                          )}
                        </div>
                      )}
                    </>
                  )}
                </div>
              );
            })()}
          </div>
        </div>
      </div>
    </div>
  );
}
