import React, { useState, useContext } from 'react';
import { tiles } from '../data/tiles';
import { GameContext } from '../context/GameContext';

const TopPropertyDisplay = () => {
  const [expandedIndex, setExpandedIndex] = useState(null);
  const { players, player: currentPlayer } = useContext(GameContext);

  // Get all players except the current player
  const otherPlayers = players.filter(p => p.socketId !== currentPlayer?.socketId);
  
  // Take the first half of other players (these will appear above the board)
  const topPlayers = otherPlayers.slice(0, Math.floor(otherPlayers.length / 2));

  // Calculate width for each player's property stack
  const stackWidth = 800 / Math.max(1, topPlayers.length); // 800px is total width

  // Reduced dimensions (70% of original)
  const cardWidth = 147; // 70% of 210
  const cardHeight = 196; // 70% of 280
  const cardOverlap = 39; // 70% of 56
  const expandOffset = 108; // 70% of 154

  return (
    <div style={{
      position: 'absolute',
      bottom: '100%',
      left: '50%',
      transform: 'translateX(-50%)',
      height: cardHeight + 'px',
      display: 'flex',
      alignItems: 'flex-end',
      overflow: 'visible',
      marginBottom: '-3px',
      width: '800px',
      justifyContent: 'space-around'
    }}>
      {topPlayers.map((player, playerIndex) => {
        // Get player's properties
        const ownedProperties = tiles
          .filter(tile => tile.type === 'property')
          .filter(tile => player?.properties?.includes(tile.id));

        return (
          <div key={player.socketId} style={{
            width: `${stackWidth}px`,
            height: '100%',
            display: 'flex',
            alignItems: 'flex-end',
            position: 'relative'
          }}>
            {/* Player name label */}
            <div style={{
              position: 'absolute',
              top: '0',
              left: '50%',
              transform: 'translateX(-50%)',
              backgroundColor: 'rgba(0, 0, 0, 0.7)',
              color: '#fff',
              padding: '4px 8px',
              borderRadius: '4px',
              whiteSpace: 'nowrap',
              fontSize: '0.9rem',
              fontWeight: 'bold',
              zIndex: 100
            }}>
              {player.name}
            </div>

            {ownedProperties.map((property, index) => {
              const isLastProperty = index === ownedProperties.length - 1;
              const isExpanded = isLastProperty || expandedIndex === index;
              
              // Calculate base position (never changes)
              const basePosition = index * cardOverlap;
              
              // Calculate additional offset based on expanded state
              let expandedOffset = 0;
              
              if (expandedIndex !== null) {
                // If there's an expanded card
                if (index > expandedIndex) {
                  expandedOffset = expandOffset;
                }
              }

              // Calculate final position
              const finalPosition = basePosition + expandedOffset;
              
              return (
                <div
                  key={property.id}
                  onClick={() => !isLastProperty && setExpandedIndex(expandedIndex === index ? null : index)}
                  style={{
                    width: cardWidth + 'px',
                    height: cardHeight + 'px',
                    border: '2px solid #666',
                    borderRadius: '8px',
                    position: 'absolute',
                    left: finalPosition,
                    transition: 'all 0.3s ease',
                    zIndex: isExpanded ? ownedProperties.length : index,
                    display: 'flex',
                    flexDirection: 'column',
                    padding: '10px',
                    boxSizing: 'border-box',
                    backgroundColor: 'rgb(173, 216, 230)',
                    cursor: isLastProperty ? 'default' : 'pointer',
                    transform: 'rotate(180deg)' // Flip cards upside down for top display
                  }}
                >
                  <div style={{
                    transform: 'rotate(90deg)', // Adjust text rotation for upside-down cards
                    transformOrigin: 'left center',
                    position: 'absolute',
                    width: cardHeight + 'px',
                    height: cardWidth + 'px',
                    top: cardHeight / 2 + 'px',
                    left: cardWidth / 2 + 'px',
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'flex-start',
                    justifyContent: 'flex-start',
                    color: '#333',
                    fontSize: '1.2rem',
                    fontWeight: 'bold',
                    padding: '10px'
                  }}>
                    <div style={{
                      fontSize: '1.4rem',
                      marginBottom: '10px',
                      textAlign: 'left',
                      whiteSpace: 'nowrap',
                      overflow: 'hidden',
                      textOverflow: 'ellipsis',
                      maxWidth: '176px',
                      width: '100%'
                    }}>
                      {property.name}
                    </div>
                    <div style={{
                      fontSize: '1rem',
                      opacity: isExpanded ? 1 : 0,
                      transition: 'opacity 0.3s ease',
                      textAlign: 'center'
                    }}>
                      <div>Cost: ${property.cost}</div>
                      <div>Rent: ${property.rent}</div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        );
      })}
    </div>
  );
};

export default TopPropertyDisplay;
