import React, { useState, useContext, useEffect } from 'react';
import { tiles } from '../data/tiles';
import { GameContext } from '../context/GameContext';

const PropertyCard = ({ property, isExpanded, isLastProperty, isCurrentPlayer, socket, playerId, player }) => {
  // Debug logs for props
  console.log('PropertyCard props:', {
    propertyName: property.name,
    isExpanded,
    isLastProperty,
    isCurrentPlayer,
    playerId,
    playerProperties: player?.properties,
    division: property.division
  });

  const handleSell = (e) => {
    e.stopPropagation(); // Prevent card expansion when clicking sell button
    console.log('Sell button clicked for:', property.name);
    if (window.confirm(`Are you sure you want to sell ${property.name} for $${property.cost}?`)) {
      socket.emit('updateProperty', {
        playerId: playerId,
        propertyId: property.id,
        action: 'remove',
        refundAmount: property.cost
      });
    }
  };

  // Calculate rent multiplier based on owned properties
  const getRentMultiplier = () => {
    console.log('Calculating multiplier for:', {
      propertyName: property.name,
      playerProperties: player?.properties,
      division: property.division
    });

    if (!player?.properties) {
      console.log('No player properties found');
      return 1;
    }

    const ownedProperties = tiles.filter(tile => player.properties.includes(tile.id));
    const divisionProperties = ownedProperties.filter(tile => tile.division === property.division);
    const count = divisionProperties.length;

    console.log('Division properties:', {
      division: property.division,
      count,
      properties: divisionProperties.map(p => p.name)
    });

    if (property.division === 'tech') {
      if (count >= 6) return 5;
      if (count >= 5) return 4;

      const hasGoogle = player.properties.includes(37);
      const hasApple = player.properties.includes(36);
      const hasAmazon = player.properties.includes(41);

      if (hasGoogle && hasApple && hasAmazon) return 3;
      if (count >= 3) return 2;
      return 1;
    } else {
      if (count >= 6) return 5;
      if (count >= 5) return 3;
      if (count >= 3) return 2;
      return 1;
    }
  };

  const multiplier = getRentMultiplier();
  console.log('Final multiplier:', {
    propertyName: property.name,
    multiplier
  });

  // Debug log for sell button visibility
  console.log('Sell button visibility:', {
    propertyName: property.name,
    isExpanded,
    isCurrentPlayer,
    shouldShow: isExpanded && isCurrentPlayer
  });

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'flex-start',
      justifyContent: 'flex-start',
      color: '#333',
      fontSize: '1.2rem',
      fontWeight: 'bold',
      height: '100%',
      position: 'relative'
    }}>
      <div
        style={{
          fontSize: '1.4rem',
          marginBottom: '10px',
          textAlign: 'left',
          whiteSpace: 'nowrap',
          overflow: 'hidden',
          textOverflow: 'ellipsis',
          maxWidth: '176px',
