import React, { useState, useContext } from 'react';
import { tiles } from '../data/tiles';
import { GameContext } from '../context/GameContext';

const PropertyDisplay = () => {
  const [expandedIndex, setExpandedIndex] = useState(null);
  const { players, currentPlayer } = useContext(GameContext);

  // Get only current player's properties
  const ownedProperties = tiles
    .filter(tile => tile.type === 'property')
    .filter(tile => currentPlayer?.properties?.includes(tile.id));

  const handlePropertyClick = (index) => {
    // Don't allow clicking the last property as it's always expanded
    if (index === ownedProperties.length - 1) return;
    setExpandedIndex(expandedIndex === index ? null : index);
  };

  return (
    <div style={{
      position: 'absolute',
      top: '100%',
      left: '50%',
      transform: 'translateX(-50%)',
      height: '200px',
      display: 'flex',
      alignItems: 'flex-start',
      overflow: 'hidden',
      marginTop: '-3px'
    }}>
      {ownedProperties.map((property, index) => {
        const isLastProperty = index === ownedProperties.length - 1;
        const isExpanded = isLastProperty || expandedIndex === index;
        const isRightOfExpanded = (expandedIndex !== null && index > expandedIndex) || 
                                 (index < ownedProperties.length - 1 && index > expandedIndex);
        const isLeftOfExpanded = (expandedIndex !== null && index < expandedIndex) || 
                                (index < ownedProperties.length - 1);

        return (
          <div
            key={property.id}
            onClick={() => handlePropertyClick(index)}
            style={{
              width: '150px',
              height: '200px',
              border: '2px solid #666',
              borderRadius: '12px',
              position: 'relative',
              cursor: isLastProperty ? 'default' : 'pointer',
              transition: 'transform 0.3s ease',
              transform: `translateX(${isRightOfExpanded ? 150 : isLeftOfExpanded ? -40 : 0}px)`,
              marginLeft: index === 0 ? 0 : '-110px',
              zIndex: isExpanded ? ownedProperties.length : ownedProperties.length - index,
              display: 'flex',
              flexDirection: 'column',
              padding: '10px',
              boxSizing: 'border-box',
              backgroundColor: 'rgba(173, 216, 230, 0.7)'
            }}
          >
            <div style={{
              transform: 'rotate(-90deg)',
              transformOrigin: 'left center',
              position: 'absolute',
              width: '200px',
              height: '150px',
              top: '100px',
              left: '75px',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'flex-start',
              justifyContent: 'flex-start',
              color: '#333',
              fontSize: '1.2rem',
              fontWeight: 'bold',
              padding: '10px'
            }}>
              <div style={{
                fontSize: '1.4rem',
                marginBottom: '10px',
                textAlign: 'left',
                whiteSpace: 'nowrap',
                overflow: 'hidden',
                textOverflow: 'ellipsis',
                maxWidth: '180px',
                width: '100%'
              }}>
                {property.name}
              </div>
              <div style={{
                fontSize: '1rem',
                opacity: isExpanded ? 1 : 0,
                transition: 'opacity 0.3s ease',
                textAlign: 'center'
              }}>
                <div>Cost: ${property.cost}</div>
                <div>Rent: ${property.rent}</div>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default PropertyDisplay;
