import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';

const DiceRoller = () => {
  const {
    socket,
    player,
    players,
    currentPlayerIndex,
    diceValues,
    setDiceValues,
    setOriginalCount,
    isMoving,
    setIsMoving,
    setCurrentPlayerIndex
  } = useContext(GameContext);

  // only the current player sees the panel
  if (!player || players[currentPlayerIndex]?.name !== player.name) return null;

  const rollDice = () => {
    if (isMoving) return;
    const d1 = Math.ceil(Math.random() * 6);
    const d2 = Math.ceil(Math.random() * 6);
    setDiceValues([d1, d2]);
    setOriginalCount(d1 + d2);
    setIsMoving(true);
  };

  const handleDone = () => {
    setIsMoving(false);
    setOriginalCount(0);
    setDiceValues([0, 0]);
    // advance to next player
    setCurrentPlayerIndex((idx) => (idx + 1) % players.length);
  };

  return (
    <div style={{
      position: 'absolute',
      top: '10%',
      left: '50%',
      transform: 'translateX(-50%)',
      background: '#fff',
      border: '2px solid #333',
      borderRadius: '12px',
      padding: '1rem',
      boxShadow: '0 4px 10px rgba(0,0,0,0.2)',
      zIndex: 1000,
      textAlign: 'center'
    }}>
      <h3>Your Turn</h3>
      {!diceValues[0] ? (
        <button onClick={rollDice}>Roll Dice</button>
      ) : (
        <>
          <div style={{ display: 'flex', justifyContent: 'center', gap: '10px', margin: '1rem 0' }}>
            <img src={require(`../assets/dice/dice${diceValues[0]}.png`)} alt={`Die ${diceValues[0]}`} width={60} />
            <img src={require(`../assets/dice/dice${diceValues[1]}.png`)} alt={`Die ${diceValues[1]}`} width={60} />
          </div>
          {!isMoving && <button onClick={handleDone}>Done</button>}
        </>
      )}
    </div>
  );
};

export default DiceRoller;
