import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';

export default function DiceRoller() {
  const {
    player,
    currentPlayerId,
    socket,
    diceRoll
  } = useContext(GameContext);

  // Only show to the active player
  if (!player || player.socketId !== currentPlayerId) return null;

  const handleRoll = () => {
    socket.emit('rollDice');
  };

  return (
    <div style={{
      position: 'absolute',
      top: '12%',
      left: '50%',
      transform: 'translateX(-50%)',
      background: '#fff',
      border: '2px solid #333',
      borderRadius: '12px',
      padding: '1rem',
      boxShadow: '0 4px 10px rgba(0,0,0,0.2)',
      zIndex: 1000,
      textAlign: 'center'
    }}>
      {diceRoll && diceRoll.playerId === player.socketId ? (
        <div style={{ display: 'flex', gap: '10px', justifyContent: 'center' }}>
          <img
            src={`/dice/dice${diceRoll.die1}.png`}
            alt={`Die ${diceRoll.die1}`}
            width={50}
          />
          <img
            src={`/dice/dice${diceRoll.die2}.png`}
            alt={`Die ${diceRoll.die2}`}
            width={50}
          />
        </div>
      ) : (
        <button onClick={handleRoll}>Roll Dice</button>
      )}
    </div>
  );
}
