  if (!p) return null;

  return (
    <div style={{
      background: '#f0f0f0',
      padding: '10px 20px',
      textAlign: 'center',
      borderTop: '2px solid #ccc',
      position: 'absolute',
      bottom: 0,
      left: 0,
      right: 0
    }}>
      <h3>{p.name}'s Dashboard</h3>
      <p>üí∞ ${p.money}</p>
      <p>üè† {p.properties.length ? p.properties.join(', ') : 'None'}</p>
    </div>
  );
};

export default Dashboard;
