import React, { useState, useEffect, useContext } from 'react';
import { GameContext } from '../context/GameContext';
import { tiles } from '../data/tiles';
import Dicebox from '../assets/diceBoard.png';

export default function DiceRoller() {
  const { player, currentPlayerId, socket } = useContext(GameContext);

  const [dice, setDice] = useState(null);
  const [done, setDone] = useState(false);
  const [branchOptions, setBranchOptions] = useState(null);

  useEffect(() => {
    const onDiceResult = ({ playerId, die1, die2 }) => {
      if (playerId === player?.socketId) {
        setDice([die1, die2]);
        setDone(false);
      }
    };
    const onBranchChoices = ({ options }) => setBranchOptions(options);
    const onMovementDone = () => setDone(true);

    socket.on('diceResult', onDiceResult);
    socket.on('branchChoices', onBranchChoices);
    socket.on('movementDone', onMovementDone);

    return () => {
      socket.off('diceResult', onDiceResult);
      socket.off('branchChoices', onBranchChoices);
      socket.off('movementDone', onMovementDone);
    };
  }, [player, socket]);

  if (!player || player.socketId !== currentPlayerId) return null;

  const handleRoll = () => {
    socket.emit('rollDice');
    setBranchOptions(null);
  };

  const handleDone = () => {
    socket.emit('endTurn');
    setDice(null);
    setDone(false);
    setBranchOptions(null);
  };

  const chooseBranch = (idx) => {
    socket.emit('branchChoice', idx);
    setBranchOptions(null);
  };

  return (
    <div
      style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        width: '100%',
        height: '100%',
        textAlign: 'center',
      }}
    >
      {/* Roll button */}
      {!dice && !done && !branchOptions && (
        <button onClick={handleRoll} style={{ padding: '8px 16px', margin: '4px' }}>
          Roll Dice
        </button>
      )}

      {/* Always show dice faces when rolled */}
      {dice && (
        <div style={{ display: 'flex', justifyContent: 'center', gap: '10px', margin: '1rem 0' }}>
          <img src={`/dice/dice${dice[0]}.png`} alt={`Die ${dice[0]}`} width={50} />
          <img src={`/dice/dice${dice[1]}.png`} alt={`Die ${dice[1]}`} width={50} />
        </div>
      )}

      {/* Branch options */}
      {branchOptions && (
        <div style={{ display: 'flex', gap: '8px', justifyContent: 'center', flexWrap: 'wrap' }}>
          {branchOptions.map((toTileId, i) => {
            const tile = tiles.find((t) => t.id === toTileId);
            const label = tile ? tile.name : `Tile ${toTileId}`;
            return (
              <button
                key={i}
                style={{ margin: '0 0.25rem', padding: '0.4rem 0.8rem' }}
                onClick={() => chooseBranch(i)}
              >
                {label}
              </button>
            );
          })}
        </div>
      )}

      {/* Done button */}
      {done && (
        <button onClick={handleDone} style={{ padding: '8px 16px', marginTop: '8px' }}>
          Done
        </button>
      )}
    </div>
  );
}
