// src/components/Dashboard.js

import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';

const Dashboard = () => {
  const { players, currentPlayerIndex } = useContext(GameContext);
  const p = players[currentPlayerIndex];
  if (!p) return null;

  // Default properties to an empty array if undefined
  const properties = Array.isArray(p.properties) ? p.properties : [];

  return (
    <div style={{
      background: '#f0f0f0',
      padding: '10px 20px',
      textAlign: 'center',
      borderTop: '2px solid #ccc',
      position: 'absolute',
      bottom: 0,
      left: 0,
      right: 0
    }}>
      <h3>{p.name}'s Dashboard</h3>
      <p>ðŸ’° ${p.money ?? 0}</p>
      <p>ðŸ  {properties.length > 0 ? properties.join(', ') : 'None'}</p>
    </div>
  );
};

export default Dashboard;
