import { useEffect, useContext } from 'react';
import { GameContext } from '../context/GameContext';
import { tiles } from '../data/tiles';

const GameController = () => {
  const {
    players, setPlayers,
    currentPlayerIndex,
    originalCount, setOriginalCount,
    isMoving, setIsMoving,
    setDiceRoll
  } = useContext(GameContext);

  useEffect(() => {
    if (!isMoving || originalCount <= 0) return;

    const initialTotal = originalCount;
    let remaining = originalCount;

    const moveStep = async () => {
      console.log(
        `[GameController] Starting move: ${initialTotal} steps for ${players[currentPlayerIndex].name}`
      );

      const updated = [...players];
      const me = updated[currentPlayerIndex];

      while (remaining > 0) {
        await new Promise(res => setTimeout(res, 500));

        const currentId = me.tileId || 1;
        const tile = tiles.find(t => t.id === currentId);
        const prev = me.prevTile;

        // Pick only those with the same `from`
        const candidates = (tile.next || []).filter(n => n.from === prev);

        let choice = null;

        if (initialTotal === 7) {
          // auto-any
          choice = candidates.find(n => n.roll === 'any');
          if (!choice && candidates.length > 1) {
            const options = candidates.map(n => n.to).join(' or ');
            const goFirst = window.confirm(
              `[GameController] Roll=7: choose path to ${options}\nOK → ${candidates[0].to}`
            );
            choice = goFirst ? candidates[0] : candidates[1];
          }
        } else if (initialTotal < 7) {
          choice = candidates.find(n => n.roll === 'below' || n.roll === 'any');
        } else {
          choice = candidates.find(n => n.roll === 'above' || n.roll === 'any');
        }

        if (!choice) {
          console.warn(
            `[GameController] No valid path from tile ${currentId} with total=${initialTotal}`
          );
          break;
        }

        me.prevTile = currentId;
        me.tileId = choice.to;
        remaining--;
        console.log(
          `[GameController] Moved to tile ${me.tileId}, ${remaining} steps left`
        );
        setPlayers([...updated]);
      }

      console.log(
        `[GameController] Move complete for ${players[currentPlayerIndex].name}`
      );
      setIsMoving(false);
      setOriginalCount(0);
      setDiceRoll(null);
    };

    moveStep();
// eslint-disable-next-line react-hooks/exhaustive-deps
  }, [isMoving]);

  return null;
};

export default GameController;
