import React, { useContext, useState, useEffect } from 'react';
import { GameContext } from '../context/GameContext';
import boardImage from '../assets/board.png';
import outerBoardImage from '../assets/outerBoard.png';
import { tiles } from '../data/tiles';
import PropertyDisplay from './PropertyDisplay';
import TopPropertyDisplay from './TopPropertyDisplay';

// Map piece names to images
const pieceImages = {};
for (let i = 1; i <= 8; i++) {
  pieceImages[`piece${i}.png`] = require(`../assets/pieces/piece${i}.png`);
}

// Desired piece height in px
const PIECE_HEIGHT = 165;

const Board = () => {
  const { players } = useContext(GameContext);
  const [boardSize, setBoardSize] = useState({ width: 600, height: 600 });

  useEffect(() => {
    const img = new window.Image();
    img.onload = () => setBoardSize({ width: img.width, height: img.height });
    img.src = boardImage;
  }, []);

  return (
    <div style={{
      position: 'relative',
      width: boardSize.width,
      height: boardSize.height,
      margin: '0 auto'
    }}>
      <TopPropertyDisplay />

      {/* Outer board image */}
      <img
        src={outerBoardImage}
        alt="Outer Board"
        style={{
          position: 'absolute',
          top: 'calc(50% + 4px)',
          left: 'calc(50% - 2px)',
          transform: 'translate(-50%, -50%)',
          width: '10%',
          height: '102%',
          zIndex: 2
        }}
      />

      {/* Main board image */}
      <div style={{
        position: 'relative',
        zIndex: 4
      }}>
        <img
          src={boardImage}
          alt="Board"
          style={{
            width: '100%',
            position: 'relative',
            zIndex: 4
          }}
        />

        {/* Player pieces */}
        {players.map((p, i) => {
          if (!p) {
            console.log(`[Board.js] Missing player at index ${i}:`, p);
            return null;
          }
          const imgSrc = pieceImages[p.piece];
          if (!imgSrc) {
            console.log(`[Board.js] Player ${p.name} has invalid piece:`, p.piece);
            return null;
          }
          const tile = tiles.find(t => t.id === p.tileId);
          if (!tile) {
            console.log(`[Board.js] Player ${p.name} on missing tileId:`, p.tileId);
            return null;
          }
          const { x, y } = tile.position;
          return (
            <img
              key={p.socketId}
              src={imgSrc}
              alt={p.name}
              title={p.name}
              style={{
                position: 'absolute',
                top: y + i * 10,
                left: x + i * 10,
                height: PIECE_HEIGHT,
                width: 'auto',
                transform: 'translate(-50%, -50%)',
                transition: 'top 0.3s, left 0.3s',
                zIndex: 10,
              }}
            />
          );
        })}
      </div>

      {/* Property Displays */}
      <PropertyDisplay />
    </div>
  );
};

export default Board;
