// src/components/PlayerStats.js
import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';

const PlayerStats = () => {
  const { players, player } = useContext(GameContext);

  return (
    <div style={{ display: 'flex', flexDirection: 'column', gap: '12px', padding: '10px' }}>
      {players
        .filter(p => p?.name && p.name !== player?.name)
        .map((p, idx) => (
          <div key={idx} style={{
            border: '1px solid black',
            padding: '10px',
            backgroundColor: '#f5f5f5',
            fontSize: '0.9rem'
          }}>
            <strong>{p.name}</strong>'s Dashboard<br />
            Money: ${p.money ?? 0}<br />
            Properties: {(p.properties ?? []).join(', ') || 'None'}
          </div>
        ))}
    </div>
  );
};

export default PlayerStats;
