import React, { useState, useContext } from 'react';
import { tiles } from '../data/tiles';
import { GameContext } from '../context/GameContext';

const PropertyDisplay = () => {
  const [expandedIndex, setExpandedIndex] = useState(null);
  const { player } = useContext(GameContext);

  // Get only current player's properties
  const ownedProperties = tiles
    .filter(tile => tile.type === 'property')
    .filter(tile => player?.properties?.includes(tile.id));

  const handlePropertyClick = (index) => {
    // Don't allow clicking the last property as it's always expanded
    if (index === ownedProperties.length - 1) return;
    setExpandedIndex(expandedIndex === index ? null : index);
  };

  return (
    <div style={{
      position: 'absolute',
      top: '100%',
      left: '50%',
      transform: 'translateX(-50%)',
      height: '200px',
      display: 'flex',
      alignItems: 'flex-start',
      overflow: 'visible',
      marginTop: '-3px',
      width: '800px',
      justifyContent: 'center'
    }}>
      {ownedProperties.map((property, index) => {
        const isLastProperty = index === ownedProperties.length - 1;
        const isExpanded = isLastProperty || expandedIndex === index;

        // Calculate position based on expanded state
        let position = index * 40; // Base position with 40px offset between cards

        if (expandedIndex !== null && !isLastProperty) {
          if (index > expandedIndex) {
            position += 70; // Push cards after expanded one further right
          }
        }

        // Always push cards before the last one left if it's not expanded
        if (isLastProperty && expandedIndex !== null && expandedIndex !== ownedProperties.length - 1) {
          position -= 70;
        }

        return (
          <div
            key={property.id}
            onClick={() => handlePropertyClick(index)}
            style={{
              width: '150px',
              height: '200px',
              border: '2px solid #666',
              borderRadius: '12px',
              position: 'absolute',
              left: position,
              cursor: isLastProperty ? 'default' : 'pointer',
              transition: 'all 0.3s ease',
              zIndex: isExpanded ? ownedProperties.length : index,
              display: 'flex',
              flexDirection: 'column',
              padding: '10px',
              boxSizing: 'border-box',
              backgroundColor: 'rgba(173, 216, 230, 0.7)'
            }}
          >
            <div style={{
              transform: 'rotate(-90deg)',
              transformOrigin: 'left center',
              position: 'absolute',
              width: '200px',
              height: '150px',
              top: '100px',
              left: '75px',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'flex-start',
              justifyContent: 'flex-start',
              color: '#333',
              fontSize: '1.2rem',
              fontWeight: 'bold',
              padding: '10px'
            }}>
              <div style={{
                fontSize: '1.4rem',
                marginBottom: '10px',
                textAlign: 'left',
                whiteSpace: 'nowrap',
                overflow: 'hidden',
                textOverflow: 'ellipsis',
                maxWidth: '180px',
                width: '100%'
              }}>
                {property.name}
              </div>
              <div style={{
                fontSize: '1rem',
                opacity: isExpanded ? 1 : 0,
                transition: 'opacity 0.3s ease',
                textAlign: 'center'
              }}>
                <div>Cost: ${property.cost}</div>
                <div>Rent: ${property.rent}</div>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default PropertyDisplay;
