import React, { useContext, useState } from 'react';
import { GameContext } from '../context/GameContext';

const DiceRoller = () => {
  const { setOriginalCount, setIsMoving } = useContext(GameContext);
  const [dice, setDice] = useState(null); // [d1, d2]

  const rollDice = () => {
    const d1 = Math.ceil(Math.random() * 6);
    const d2 = Math.ceil(Math.random() * 6);
    setDice([d1, d2]);
    setOriginalCount(d1 + d2);
    setIsMoving(true);
  };

  return (
    <div style={{ marginTop: '1rem' }}>
      {!dice ? (
        <button onClick={rollDice}>Roll Dice</button>
      ) : (
        <div style={{ display: 'flex', justifyContent: 'center', gap: '1rem' }}>
          <img src={`/dice/dice${dice[0]}.png`} alt={`Die ${dice[0]}`} width={50} />
          <img src={`/dice/dice${dice[1]}.png`} alt={`Die ${dice[1]}`} width={50} />
        </div>
      )}
    </div>
  );
};

export default DiceRoller;
