import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';

const Dashboard = () => {
  const {
    players,
    currentPlayerIndex,
    setCurrentPlayerIndex,
    isMoving
  } = useContext(GameContext);

  const me = players[currentPlayerIndex];
  if (!me) return null;

  // only show ‚ÄúNext Turn‚Äù once I‚Äôve moved at least one step
  const hasMoved = me.prevTile !== null;

  const handleNext = () =>
    setCurrentPlayerIndex(prev => (prev + 1) % players.length);

  return (
    <div style={{
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      background: '#fff',
      padding: '1rem',
      borderTop: '1px solid #ddd',
      boxShadow: '0 -2px 5px rgba(0,0,0,0.1)'
    }}>
      <div>
        <h2 style={{ margin: 0 }}>{me.name}</h2>
        <p style={{ margin: '0.25rem 0' }}>üí∞ ${me.money}</p>
        <p style={{ margin: '0.25rem 0' }}>
          üè† {me.properties.length ? me.properties.join(', ') : 'None'}
        </p>
      </div>
      {hasMoved && !isMoving && (
        <button
          onClick={handleNext}
          style={{
            padding: '0.5rem 1rem',
            borderRadius: 4,
            border: '1px solid #888',
            background: '#f0f0f0',
            cursor: 'pointer'
          }}
        >
          Next Turn
        </button>
      )}
    </div>
  );
};

export default Dashboard;
