import React, { useContext, useState, useEffect } from 'react';
import { GameContext } from '../context/GameContext';
import io from 'socket.io-client';

import piece1 from '../assets/pieces/piece1.png';
import piece2 from '../assets/pieces/piece2.png';
import piece3 from '../assets/pieces/piece3.png';
import piece4 from '../assets/pieces/piece4.png';
import piece5 from '../assets/pieces/piece5.png';
import piece6 from '../assets/pieces/piece6.png';
import piece7 from '../assets/pieces/piece7.png';
import piece8 from '../assets/pieces/piece8.png';

const pieces = [
  { name: 'piece1', image: piece1 },
  { name: 'piece2', image: piece2 },
  { name: 'piece3', image: piece3 },
  { name: 'piece4', image: piece4 },
  { name: 'piece5', image: piece5 },
  { name: 'piece6', image: piece6 },
  { name: 'piece7', image: piece7 },
  { name: 'piece8', image: piece8 }
];

const socket = io('http://localhost:3001');

const Lobby = () => {
  const { player, setPlayer, players, setPlayers, setGameState } = useContext(GameContext);
  const [name, setName] = useState('');
  const [joined, setJoined] = useState(false);
  const [hasClickedStart, setHasClickedStart] = useState(false);

  useEffect(() => {
    socket.on('lobbyUpdate', (updatedPlayers) => {
      setPlayers(updatedPlayers);
    });

    socket.on('gameStart', () => {
      setGameState('playing');
    });

    socket.on('lobbyCleared', () => {
      setPlayer(null);
      setPlayers([]);
      setJoined(false);
      setHasClickedStart(false);
      setGameState('lobby');
    });

    return () => {
      socket.off('lobbyUpdate');
      socket.off('gameStart');
      socket.off('lobbyCleared');
    };
  }, [setPlayers, setGameState, setPlayer]);

  const joinLobby = () => {
    if (!name.trim()) return;
    const newPlayer = {
      name,
      money: 1500,
      properties: [],
      position: { x: 0, y: 0 },
      color: getRandomColor(),
    };
    setPlayer(newPlayer);
    socket.emit('joinLobby', newPlayer);
    setJoined(true);
  };

  const selectPiece = (pieceName) => {
    socket.emit('selectPiece', pieceName);
    setPlayer(prev => ({ ...prev, piece: pieceName }));
  };

  const signalReady = () => {
    socket.emit('playerReady');
    setHasClickedStart(true);
  };

  const exitLobby = () => {
    socket.emit('exitLobby');
  };

  const getRandomColor = () => {
    const colors = ['red', 'blue', 'green', 'purple', 'orange', 'yellow', 'pink', 'brown'];
    return colors[Math.floor(Math.random() * colors.length)];
  };

  const takenPieces = players.map(p => p.piece);
  const availablePieces = pieces.filter(p => !takenPieces.includes(p.name));

  return (
    <div style={{
    maxWidth: '800px',
    margin: '60px auto',
    textAlign: 'center',
    fontSize: '1.1rem',
    padding: '20px',
      }}>
      {!joined ? (
        <>
          <h2>Join Lobby</h2>
          <input value={name} onChange={(e) => setName(e.target.value)} placeholder="Enter your name" />
          <button onClick={joinLobby}>Join Lobby</button>
        </>
      ) : (
        <>
          <h2>Lobby - Waiting Room</h2>
          <ul>
            {players.map((p, i) => (
              <li key={i}>{p.name} - {p.piece || 'No piece'} {p.ready ? '✅' : '⏳'}</li>
            ))}
          </ul>

          {!player.piece && (
            <>
              <h3>Select Your Piece</h3>
              <div style={{ display: 'flex', justifyContent: 'center', flexWrap: 'wrap' }}>
                {availablePieces.map((p, i) => (
                  <button key={i} onClick={() => selectPiece(p.name)} style={{ margin: 10 }}>
                    <img src={p.image} alt={p.name} width="60" />
                  </button>
                ))}
              </div>
            </>
          )}

          {player.piece && !hasClickedStart && (
            <button onClick={signalReady}>Start Game</button>
          )}

          <br />
          <button onClick={exitLobby} style={{ marginTop: '20px' }}>Exit Lobby</button>
        </>
      )}
    </div>
  );
};

export default Lobby;
