// client/src/context/GameContext.js
import React, { createContext, useState, useEffect } from 'react';
import socket from '../socket';

export const GameContext = createContext();

export function GameProvider({ children }) {
  const [players, setPlayers] = useState([]);
  const [currentPlayerId, setCurrentPlayerId] = useState(null);
  const [sessionId, setSessionId] = useState(null);

  useEffect(() => {
    socket.on('lobbyUpdate', setPlayers);
    socket.on('gameStart', ({ players: ps, sessionId }) => {
      setPlayers(ps);
      setSessionId(sessionId);
    });
    socket.on('turnEnded', ({ nextPlayerId }) => {
      setCurrentPlayerId(nextPlayerId);
    });
    socket.on('diceResult', ({ playerId, die1, die2, total }) => {
      // you can stash last roll if you want…
    });
    socket.on('playerMoved', ({ playerId, tileId }) => {
      setPlayers(ps => ps.map(p =>
        p.id === playerId ? { ...p, tileId } : p
      ));
    });

    return () => socket.off();
  }, []);

  return (
    <GameContext.Provider
      value={{ players, currentPlayerId, sessionId, socket }}
    >
      {children}
    </GameContext.Provider>
  );
}
