import React, { useState, useContext } from 'react';
import { tiles } from '../data/tiles';
import { GameContext } from '../context/GameContext';

const TopPropertyDisplay = () => {
  const [expandedStates, setExpandedStates] = useState({});
  const { players, player: currentPlayer } = useContext(GameContext);

  // Get all players except the current player
  const otherPlayers = players.filter(p => p.socketId !== currentPlayer?.socketId);

  // Get positions based on number of players (matching PlayerStats logic)
  const getPositions = (numPlayers) => {
    switch(numPlayers) {
      case 3: return [{ left: '50%' }];
      case 4: return [{ left: '30%' }, { left: '70%' }];
      case 5: return [{ left: '50%' }];
      case 6: return [{ left: '30%' }, { left: '70%' }];
      case 7: return [{ left: '15%' }, { left: '50%' }, { left: '85%' }];
      default: return [];
    }
  };

  // Get only the players that should appear at the top
  const getTopPlayers = (players, totalPlayers) => {
    switch(totalPlayers) {
      case 3: return players.slice(2, 3);
      case 4: return players.slice(2, 4);
      case 5: return players.slice(4, 5);
      case 6: return players.slice(4, 6);
      case 7: return players.slice(4, 7);
      default: return [];
    }
  };

  const handlePropertyClick = (playerId, propertyIndex) => {
    setExpandedStates(prev => ({
      ...prev,
      [playerId]: prev[playerId] === propertyIndex ? null : propertyIndex
    }));
  };

  const topPlayers = getTopPlayers(otherPlayers, otherPlayers.length);
  const positions = getPositions(otherPlayers.length);

  // Reduced dimensions (70% of original)
  const cardWidth = 147; // 70% of 210
  const cardHeight = 196; // 70% of 280
  const cardOverlap = 39; // 70% of 56
  const expandOffset = 108; // 70% of 154
  const stackWidth = cardWidth + (cardOverlap * 4); // Width for each player's stack

  return (
    <div style={{
      position: 'absolute',
      top: '-190px',
      left: '50%',
      transform: 'translateX(-50%)',
      height: cardHeight + 'px',
      display: 'flex',
      alignItems: 'flex-start',
      overflow: 'visible',
      width: '1000px',
      justifyContent: 'space-around',
      zIndex: 1
    }}>
      {topPlayers.map((player, playerIndex) => {
        // Get player's properties
        const ownedProperties = tiles
          .filter(tile => tile.type === 'property')
          .filter(tile => player?.properties?.includes(tile.id));

        const position = positions[playerIndex];
        const playerExpandedIndex = expandedStates[player.socketId];

        return (
          <div key={player.socketId} style={{
            position: 'absolute',
            left: position.left,
            transform: 'translateX(-50%)',
            width: stackWidth + 'px',
            height: '100%',
            display: 'flex',
            alignItems: 'flex-start'
          }}>
            {/* Player name label */}
            <div style={{
              position: 'absolute',
              top: '100%',
              left: '50%',
              transform: 'translateX(-50%)',
              backgroundColor: 'rgba(0, 0, 0, 0.7)',
              color: '#fff',
              padding: '4px 8px',
              borderRadius: '4px',
              whiteSpace: 'nowrap',
              fontSize: '0.9rem',
              fontWeight: 'bold',
              zIndex: 4,
              marginTop: '4px'
            }}>
              {player.name}
            </div>

            {ownedProperties.map((property, index) => {
              const isLastProperty = index === ownedProperties.length - 1;
              const isExpanded = isLastProperty || playerExpandedIndex === index;

              // Calculate base position (never changes)
              const basePosition = index * cardOverlap;

              // Calculate additional offset based on expanded state
              let expandedOffset = 0;

              if (playerExpandedIndex !== null && playerExpandedIndex !== undefined) {
                // If there's an expanded card
                if (index > playerExpandedIndex) {
                  expandedOffset = expandOffset;
                }
              }

              // Calculate final position
              const finalPosition = basePosition + expandedOffset;

              return (
                <div
                  key={property.id}
                  onClick={() => !isLastProperty && handlePropertyClick(player.socketId, index)}
                  style={{
                    width: cardWidth + 'px',
                    height: cardHeight + 'px',
                    border: '2px solid #666',
                    borderRadius: '8px',
                    position: 'absolute',
                    left: finalPosition,
                    transition: 'all 0.3s ease',
                    zIndex: isExpanded ? ownedProperties.length : index,
                    display: 'flex',
                    flexDirection: 'column',
                    padding: '10px',
                    boxSizing: 'border-box',
                    backgroundColor: 'rgb(173, 216, 230)',
                    cursor: isLastProperty ? 'default' : 'pointer'
                  }}
                >
                  <div style={{
                    transform: 'rotate(-90deg)',
                    transformOrigin: 'left center',
                    position: 'absolute',
                    width: cardHeight + 'px',
                    height: cardWidth + 'px',
                    top: cardHeight / 2 + 'px',
                    left: cardWidth / 2 + 'px',
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'flex-start',
                    justifyContent: 'flex-start',
                    color: '#333',
                    fontSize: '1.2rem',
                    fontWeight: 'bold',
                    padding: '10px'
                  }}>
                    <div style={{
                      fontSize: '1.4rem',
                      marginBottom: '10px',
                      textAlign: 'left',
                      whiteSpace: 'nowrap',
                      overflow: 'hidden',
                      textOverflow: 'ellipsis',
                      maxWidth: '176px',
                      width: '100%'
                    }}>
                      {property.name}
                    </div>
                    <div style={{
                      fontSize: '1rem',
                      opacity: isExpanded ? 1 : 0,
                      transition: 'opacity 0.3s ease',
                      textAlign: 'left'
                    }}>
                      <div>Cost: ${property.cost}</div>
                      <div>Rent: ${property.rent}</div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        );
      })}
    </div>
  );
};

export default TopPropertyDisplay;
