// src/components/DiceRoller.js
import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';

const DiceRoller = () => {
  const {
    player,
    players,
    currentPlayerIndex,
    isMoving,
    diceRoll,
    setDiceRoll,
    setOriginalCount,
    setIsMoving
  } = useContext(GameContext);

  // Only render on your turn, before rolling or while displaying the roll
  if (!player || players[currentPlayerIndex]?.name !== player.name) {
    return null;
  }

  const rollDice = () => {
    const d1 = Math.ceil(Math.random() * 6);
    const d2 = Math.ceil(Math.random() * 6);
    const total = d1 + d2;
    console.log(`[DiceRoller] ${player.name} rolled ${d1} + ${d2} = ${total}`);
    setDiceRoll([d1, d2]);
    setOriginalCount(total);
    setIsMoving(true);
  };

  return (
    <div style={{
      position: 'absolute',
      top: '10%',
      left: '50%',
      transform: 'translateX(-50%)',
      background: '#fff',
      border: '2px solid #333',
      borderRadius: '12px',
      padding: '1rem',
      boxShadow: '0 4px 10px rgba(0,0,0,0.2)',
      zIndex: 1000,
      textAlign: 'center'
    }}>
      {/* Show roll button only if no roll yet and not moving.
          Once rolled, show dice faces while movement is in progress,
          then clear on move completion (in GameController). */}
      {!diceRoll && !isMoving && (
        <button onClick={rollDice}>Roll Dice</button>
      )}

      {diceRoll && (
        <div style={{ marginTop: '1rem', display: 'flex', justifyContent: 'center', gap: '10px' }}>
          <img
            src={`/dice/dice${diceRoll[0]}.png`}
            alt={`Die ${diceRoll[0]}`}
            width={50}
          />
          <img
            src={`/dice/dice${diceRoll[1]}.png`}
            alt={`Die ${diceRoll[1]}`}
            width={50}
          />
        </div>
      )}
    </div>
  );
};

export default DiceRoller;
