// src/components/DiceRoller.js
import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';

const DiceRoller = () => {
  const {
    currentPlayerIndex,
    players,
    player,
    rollDice,
    diceValues,
    isMoving
  } = useContext(GameContext);

  const isMyTurn = players[currentPlayerIndex]?.name === player.name;

  if (!isMyTurn || isMoving) return null;

  const [d1, d2] = diceValues;
  const total = d1 + d2;

  return (
    <div style={{
      position: 'absolute',
      top: '20%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      background: 'white',
      padding: '20px',
      border: '2px solid black',
      textAlign: 'center',
      zIndex: 10
    }}>
      <h2>Your Turn</h2>
      <div style={{ display: 'flex', justifyContent: 'center', gap: '15px' }}>
        <img src={`/dice/dice${d1}.png`} alt={`Die ${d1}`} width="80" />
        <img src={`/dice/dice${d2}.png`} alt={`Die ${d2}`} width="80" />
      </div>
      <h3>Total: {total}</h3>
      <button onClick={rollDice} style={{ marginTop: '10px' }}>Roll Dice</button>
    </div>
  );
};

export default DiceRoller;
