import React, { useContext, useState, useEffect } from 'react';
import { GameContext } from '../context/GameContext';
import io from 'socket.io-client';
import './Lobby.css';

const socket = io('http://localhost:3001');
const pieceNames = [
  'piece1','piece2','piece3','piece4',
  'piece5','piece6','piece7','piece8'
];

const Lobby = () => {
  const { player, setPlayer, players, setPlayers, setGameState } = useContext(GameContext);
  const [name, setName] = useState('');
  const [selected, setSelected] = useState('');
  const [readyPlayers, setReadyPlayers] = useState([]);

  useEffect(() => {
    socket.on('lobbyUpdate', pl => setPlayers(pl));
    socket.on('readyUpdate', ready => setReadyPlayers(ready));
    socket.on('gameStart', () => setGameState('playing'));
    return () => {
      socket.off('lobbyUpdate');
      socket.off('readyUpdate');
      socket.off('gameStart');
    };
  }, [setPlayers, setGameState]);

  const joinLobby = () => {
    if (!name.trim()) return;
    const newPlayer = {
      name,
      piece: '',
      ready: false,
      tileId: 1,
      prevTile: null,
      money: 1500,
      properties: []
    };
    setPlayer(newPlayer);
    socket.emit('joinLobby', newPlayer);
  };

  const choosePiece = piece => {
    setSelected(piece);
    setPlayer(p => ({ ...p, piece }));
    socket.emit('selectPiece', { name, piece });
  };

  const handleReady = () => socket.emit('playerReady', name);

  const used = players.map(p => p.piece).filter(Boolean);
  const available = pieceNames.filter(p => !used.includes(p));
  const allReady = players.length >= 2
    && players.every(p => p.piece && readyPlayers.includes(p.name));

  return (
    <div style={{ textAlign: 'center', padding: '1rem', maxWidth: 400, margin: 'auto' }}>
      <h2>Monopoly Lobby</h2>

      {!player && (
        <>
          <input
            type="text"
            placeholder="Enter your name"
            value={name}
            onChange={e => setName(e.target.value)}
            style={{ padding: '0.5rem', width: '100%' }}
          />
          <button
            onClick={joinLobby}
            style={{ marginTop: '1rem', padding: '0.5rem 1rem' }}
          >
            Join
          </button>
        </>
      )}

      {player && !selected && (
        <div style={{ marginTop: '1rem' }}>
          <p>Select your piece:</p>
          {available.map(p => (
            <img
              key={p}
              src={require(`../assets/pieces/${p}.png`)}
              alt={p}
              style={{ width: 40, margin: 5, cursor: 'pointer' }}
              onClick={() => choosePiece(p)}
            />
          ))}
        </div>
      )}

      {selected && !readyPlayers.includes(name) && (
        <button
          onClick={handleReady}
          style={{ marginTop: '1rem', padding: '0.5rem 1rem' }}
        >
          Ready âœ…
        </button>
      )}

      <h3 style={{ marginTop: '2rem' }}>Players</h3>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {players.map((p,i) => (
          <li key={i} style={{ margin: '0.5rem 0' }}>
            {p.name}
            {p.piece && (
              <img
                src={require(`../assets/pieces/${p.piece}.png`)}
                alt=""
                style={{ width: 20, verticalAlign: 'middle', marginLeft: 4 }}
              />
            )}
            {readyPlayers.includes(p.name) && ' âœ…'}
          </li>
        ))}
      </ul>

      {allReady && <p style={{ color: 'green' }}>All set! Startingâ€¦</p>}
    </div>
  );
};

export default Lobby;
