// src/components/DiceRoller.js
import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';

const DiceRoller = () => {
  const {
    player, currentPlayerIndex, players,
    originalCount, setOriginalCount,
    isMoving, setIsMoving
  } = useContext(GameContext);

  const isPlayerTurn = players[currentPlayerIndex]?.id === player.id;

  const rollDice = () => {
    if (!isPlayerTurn || isMoving) return;

    const d1 = Math.ceil(Math.random() * 6);
    const d2 = Math.ceil(Math.random() * 6);
    const total = d1 + d2;
    setOriginalCount(total);
    setIsMoving(true);
  };

  if (!isPlayerTurn) return null;

  const d1 = originalCount ? Math.floor(originalCount / 2) : 0;
  const d2 = originalCount ? originalCount - d1 : 0;

  return (
    <div style={{
      position: 'absolute',
      top: '20%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      background: 'white',
      padding: 16,
      border: '1px solid black',
      borderRadius: 8,
      zIndex: 1000
    }}>
      <strong>Your Turn</strong><br />
      {!originalCount && <button onClick={rollDice}>Roll Dice</button>}
      {originalCount > 0 && (
        <div>
          <p>Dice</p>
          <img src={`/assets/dice/dice${d1}.png`} alt={`Dice ${d1}`} width={40} />
          <img src={`/assets/dice/dice${d2}.png`} alt={`Dice ${d2}`} width={40} />
        </div>
      )}
    </div>
  );
};

export default DiceRoller;
