import React, { useContext, useEffect } from 'react';
import { GameContext } from '../context/GameContext';
import { tiles } from '../data/tiles';


const GameController = () => {
  const {
    players, setPlayers,
    currentPlayerIndex, setCurrentPlayerIndex,
    originalCount, setOriginalCount,
    isMoving, setIsMoving
  } = useContext(GameContext);

  useEffect(() => {
    if (!isMoving || originalCount <= 0) return;

    const movePlayer = async () => {
      let count = originalCount;
      const player = players[currentPlayerIndex];

      while (count > 0) {
        await new Promise(res => setTimeout(res, 500));

        const currentTile = tiles[player.currentTile || 0];
        let nextId;

        if (originalCount === 7) {
          nextId = window.confirm("Go Above 7 Path?") ? currentTile.nextAbove7 : currentTile.nextBelow7;
        } else {
          nextId = originalCount < 7 ? currentTile.nextBelow7 : currentTile.nextAbove7;
        }

        player.prevTile = player.currentTile;
        player.currentTile = nextId;
        count--;

        setPlayers([...players]);
        setOriginalCount(count);
      }

      setIsMoving(false);
    };

    movePlayer();
  }, [isMoving]);

  return null;
};

export default GameController;
