import React, { useContext, useState, useEffect } from 'react';
import { GameContext } from '../context/GameContext';
import io from 'socket.io-client';

const socket = io('http://localhost:3001');
const allPieces = [
  'piece1.png','piece2.png','piece3.png','piece4.png',
  'piece5.png','piece6.png','piece7.png','piece8.png'
];

const Lobby = () => {
  const { player, setPlayer, players, setPlayers, setGameState } = useContext(GameContext);
  const [name, setName] = useState('');
  const [selected, setSelected] = useState('');
  const [readyList, setReadyList] = useState([]);

  useEffect(() => {
    socket.on('lobbyUpdate', setPlayers);
    socket.on('readyUpdate', setReadyList);
    socket.on('gameStart', () => setGameState('playing'));
    return () => {
      socket.off('lobbyUpdate');
      socket.off('readyUpdate');
      socket.off('gameStart');
    };
  }, [setPlayers, setGameState]);

  const join = () => {
    if (!name.trim()) return;
    const me = { name, piece: null, ready: false, tileId: 1 };
    setPlayer(me);
    socket.emit('joinLobby', me);
  };

  const pick = (piece) => {
    setSelected(piece);
    const updated = { ...player, piece };
    setPlayer(updated);
    socket.emit('selectPiece', updated);
  };

  const ready = () => {
    socket.emit('playerReady', player.name);
  };

  const used = players.map(p => p.piece).filter(Boolean);
  const available = allPieces.filter(p => !used.includes(p));
  const allReady = players.length >= 2 && players.every(p => p.piece && readyList.includes(p.name));

  return (
    <div style={{
      display: 'flex', flexDirection: 'column',
      alignItems: 'center', justifyContent: 'center',
      height: '100vh', fontSize: '1.2rem'
    }}>
      <h2>Monopoly Lobby</h2>
      {!player && (
        <>
          <input
            placeholder="Enter your name"
            value={name}
            onChange={e => setName(e.target.value)}
            style={{ marginBottom: 10 }}
          />
          <button onClick={join}>Join</button>
        </>
      )}
      {player && !selected && (
        <div>
          <p>Select a piece:</p>
          <div style={{ display: 'flex', gap: 10 }}>
            {available.map(p => (
              <img
                key={p}
                src={`/assets/pieces/${p}`}
                alt={p}
                width={50}
                style={{ cursor: 'pointer' }}
                onClick={() => pick(p)}
              />
            ))}
          </div>
        </div>
      )}
      {selected && !readyList.includes(player.name) && (
        <button onClick={ready} style={{ marginTop: 20 }}>Ready</button>
      )}
      <hr style={{ width: '60%', margin: '2rem 0' }} />
      <h3>Players ({players.length}):</h3>
      <ul>
        {players.map((p, i) => (
          <li key={i}>
            {p.name} {p.piece && `ðŸ§© ${p.piece}`} {readyList.includes(p.name) && 'âœ…'}
          </li>
        ))}
      </ul>
      {allReady && <p style={{ color: 'green' }}>All ready â€” starting!</p>}
    </div>
  );
};

export default Lobby;
