// src/components/Board.js
import React, { useContext, useState, useEffect } from 'react';
import { GameContext } from '../context/GameContext';
import boardImage from '../assets/board.png';
import { tiles } from '../data/tiles';

import piece1 from '../assets/pieces/piece1.png';
import piece2 from '../assets/pieces/piece2.png';
import piece3 from '../assets/pieces/piece3.png';
import piece4 from '../assets/pieces/piece4.png';
import piece5 from '../assets/pieces/piece5.png';
import piece6 from '../assets/pieces/piece6.png';
import piece7 from '../assets/pieces/piece7.png';
import piece8 from '../assets/pieces/piece8.png';

const pieceMap = {
  piece1, piece2, piece3, piece4, piece5, piece6, piece7, piece8,
};

const Board = () => {
  const { players } = useContext(GameContext);
  const [coords, setCoords] = useState({ x: null, y: null });
  const [naturalSize, setNaturalSize] = useState({ width: 600, height: 600 });

  useEffect(() => {
    const img = new Image();
    img.onload = () => {
      setNaturalSize({ width: img.width, height: img.height });
    };
    img.src = boardImage;
  }, []);

  const handleClick = (e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const rawX = e.clientX - rect.left;
    const rawY = e.clientY - rect.top;

    const logicalX = Math.round(rawX);
    const logicalY = Math.round(rawY);

    console.log(`Tile Coordinates: (${logicalX}, ${logicalY})`);
    setCoords({ x: logicalX, y: logicalY });
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '20px' }}>
      <div
        onClick={handleClick}
        style={{
          position: 'relative',
          width: `${naturalSize.width}px`,
          height: `${naturalSize.height}px`,
          backgroundImage: `url(${boardImage})`,
          backgroundSize: '100% 100%',
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
          border: '3px solid #333',
          overflow: 'visible',
          margin: '0 auto',
        }}
      >
        {players.map((p, i) => {
          const pieceImg = pieceMap[p.piece];
          if (!pieceImg) return null;

          const tile = tiles.find(t => t.id === p.tileId);
          const pos = tile?.location || { x: 40, y: 520 }; // fallback to start position

          return (
            <img
              key={p.name}
              src={pieceImg}
              alt={p.name}
              title={p.name}
              style={{
                position: 'absolute',
                top: pos.y + i * 10,
                left: pos.x + i * 10,
                width: 30,
                height: 30,
                transform: 'translate(-50%, -50%)',
                transition: 'top 0.3s, left 0.3s',
              }}
            />
          );
        })}

        {coords.x !== null && (
          <div
            style={{
              position: 'absolute',
              top: 10,
              left: 10,
              backgroundColor: 'rgba(255, 255, 255, 0.9)',
              padding: '4px 8px',
              fontSize: '14px',
              border: '1px solid #ccc',
              borderRadius: '4px',
            }}
          >
            Tile: ({coords.x}, {coords.y})
          </div>
        )}
      </div>
    </div>
  );
};

export default Board;
