import { useEffect, useContext } from 'react';
import { GameContext } from '../context/GameContext';
import { tiles } from '../data/tiles';

const GameController = () => {
  const {
    players, setPlayers,
    currentPlayerIndex,
    originalCount, setOriginalCount,
    isMoving, setIsMoving
  } = useContext(GameContext);

  useEffect(() => {
    if (!isMoving || originalCount <= 0) return;

    const movePlayer = async () => {
      let count = originalCount;
      const updatedPlayers = [...players];
      const player = updatedPlayers[currentPlayerIndex];

      const excludePrev = (choices) =>
        choices.filter(id => id !== player.prevTile);

      while (count > 0) {
        await new Promise(res => setTimeout(res, 500));

        const currentTile = tiles[player.currentTile ?? 0];

        let nextId;
        if (originalCount === 7) {
          const options = excludePrev([currentTile.nextAbove7, currentTile.nextBelow7]);

          // If both options are valid, ask the player; otherwise default
          if (options.length === 2) {
            nextId = window.confirm("Go left path?") ? options[0] : options[1];
          } else {
            nextId = options[0]; // Only one valid path
          }
        } else {
          nextId = originalCount < 7
            ? currentTile.nextBelow7
            : currentTile.nextAbove7;
        }

        player.prevTile = player.currentTile;
        player.currentTile = nextId;
        count--;

        setPlayers([...updatedPlayers]);
        setOriginalCount(count);
      }

      setIsMoving(false);
    };

    movePlayer();
  }, [
    isMoving,
    originalCount,
    players,
    currentPlayerIndex,
    setPlayers,
    setOriginalCount,
    setIsMoving
  ]);

  return null;
};

export default GameController;
