import React, { createContext, useState, useEffect } from 'react';
import socket from '../socket';

export const GameContext = createContext();

export function GameProvider({ children }) {
  const [player, setPlayer]               = useState(null);
  const [players, setPlayers]             = useState([]);
  const [currentPlayerId, setCurrentPlayerId] = useState(null);
  const [sessionId, setSessionId]         = useState(null);
  const [gameState, setGameState]         = useState('lobby');

  // dice + movement flags
  const [diceRoll, setDiceRoll]           = useState(null);
  const [movementDone, setMovementDone]   = useState(false);

  // buy/rent UI
  const [insufficientFunds, setInsufficientFunds] = useState(false);

  useEffect(() => {
    // LOBBY UPDATE
    socket.on('lobbyUpdate', updated => {
      setPlayers(updated);
      const me = updated.find(p => p.socketId === socket.id);
      if (me) setPlayer(me);
    });

    // GAME START
    socket.on('gameStart', ({ players: ps, sessionId: sid, currentPlayerId: cid }) => {
      setPlayers(ps);
      setSessionId(sid);
      setGameState('playing');
      setCurrentPlayerId(cid);
      setDiceRoll(null);
      setMovementDone(false);
      setInsufficientFunds(false);

      // Set the current player's state when reconnecting
      const me = ps.find(p => p.socketId === socket.id);
      if (me) setPlayer(me);
    });

    // TURN ENDED
    socket.on('turnEnded', ({ nextPlayerId }) => {
      setCurrentPlayerId(nextPlayerId);
      setDiceRoll(null);
      setMovementDone(false);
      setInsufficientFunds(false);
    });

    // DICE RESULT
    socket.on('diceResult', ({ playerId, die1, die2, total }) => {
      setDiceRoll({ playerId, die1, die2, total });
    });

    // TILE MOVED
    socket.on('playerMoved', ({ playerId, tileId }) => {
      setPlayers(prev =>
        prev.map(p => p.socketId === playerId ? { ...p, tileId } : p)
      );
    });

    // MOVEMENT DONE
    socket.on('movementDone', () => {
      setMovementDone(true);
    });

    // INSUFFICIENT FUNDS
    socket.on('insufficientFunds', () => {
      setInsufficientFunds(true);
    });

    // RENT PAID
    socket.on('rentPaid', ({ payerSocketId, payerMoney, ownerSocketId, ownerMoney }) => {
      console.log('[GameContext] Updating money after rent payment:', {
        payerSocketId,
        payerMoney,
        ownerSocketId,
        ownerMoney
      });

      // Update all players' money
      setPlayers(prev => prev.map(p => {
        if (p.socketId === payerSocketId) return { ...p, money: payerMoney };
        if (p.socketId === ownerSocketId) return { ...p, money: ownerMoney };
        return p;
      }));

      // Update current player's money if they were involved
      if (player?.socketId === payerSocketId) {
        setPlayer(prev => ({ ...prev, money: payerMoney }));
      } else if (player?.socketId === ownerSocketId) {
        setPlayer(prev => ({ ...prev, money: ownerMoney }));
      }
    });

    return () => {
      socket.off('lobbyUpdate');
      socket.off('gameStart');
      socket.off('turnEnded');
      socket.off('diceResult');
      socket.off('playerMoved');
      socket.off('movementDone');
      socket.off('insufficientFunds');
      socket.off('rentPaid');
    };
  }, [player?.socketId]);

  return (
    <GameContext.Provider
      value={{
        player,
        setPlayer,
        players,
        setPlayers,
        currentPlayerId,
        setCurrentPlayerId,
        sessionId,
        setSessionId,
        socket,
        gameState,
        setGameState,
        diceRoll,
        setDiceRoll,
        movementDone,
        setMovementDone,
        insufficientFunds,
        setInsufficientFunds,
      }}
    >
      {children}
    </GameContext.Provider>
  );
}
