import React, { useContext, useState, useEffect } from 'react';
import { GameContext } from '../context/GameContext';
import io from 'socket.io-client';

const socket = io('http://localhost:3001');
const allPieces = ['piece1.png','piece2.png','piece3.png','piece4.png',
                   'piece5.png','piece6.png','piece7.png','piece8.png'];

const Lobby = () => {
  const { player, setPlayer, players, setPlayers, setGameState } = useContext(GameContext);
  const [name, setName] = useState('');
  const [hasJoined, setHasJoined] = useState(false);
  const [selectedPiece, setSelectedPiece] = useState('');
  const [readyPlayers, setReadyPlayers] = useState([]);

  useEffect(() => {
    socket.on('lobbyUpdate', updated => setPlayers(updated));
    socket.on('readyUpdate', setReadyPlayers);
    socket.on('gameStart', () => setGameState('playing'));
    return () => {
      socket.off('lobbyUpdate');
      socket.off('readyUpdate');
      socket.off('gameStart');
    };
  }, [setPlayers, setGameState]);

  const joinLobby = () => {
    if (!name.trim()) return;
    const newPlayer = {
      name,
      piece: null,
      ready: false,
      tileId: 1,
      money: 1500,
      properties: [],
    };
    setPlayer(newPlayer);
    setHasJoined(true);
    socket.emit('joinLobby', newPlayer);
  };

  const choosePiece = (piece) => {
    const updated = { ...player, piece };
    setSelectedPiece(piece);
    setPlayer(updated);
    socket.emit('selectPiece', { name: player.name || name, piece });
  };

  const handleReady = () => {
    socket.emit('playerReady', player.name || name);
  };

  const used = players.map(p => p.piece).filter(Boolean);
  const available = allPieces.filter(p => !used.includes(p));

  const allReady = players.length >= 2 &&
    players.every(p => p.piece && readyPlayers.includes(p.name));

  return (
    <div style={{ display:'flex', flexDirection:'column', alignItems:'center', justifyContent:'center', height:'100vh', fontSize:'1.2rem' }}>
      <h2>Monopoly Lobby</h2>
      {!hasJoined && <>
        <input placeholder="Enter your name" value={name} onChange={e => setName(e.target.value)} />
        <button onClick={joinLobby}>Join Lobby</button>
      </>}
      {hasJoined && !selectedPiece && <>
        <p>Select your piece:</p>
        <div style={{ display:'flex', gap:10 }}>
          {available.map((piece, idx) =>
            <img key={idx} src={`/pieces/${piece}`} alt={piece} width={60} onClick={() => choosePiece(piece)} style={{cursor:'pointer'}} />
          )}
        </div>
      </>}
      {selectedPiece && <>
        <p>Welcome, <strong>{player.name || name}</strong>! You've selected {selectedPiece}</p>
        <button onClick={handleReady}>Ready</button>
      </>}
      <hr style={{width:'60%', margin:'2rem 0'}}/>
      <h3>Players in Lobby:</h3>
      <ul>
        {players.map((p, i) => (
          <li key={i}>
            {p.name} {p.piece && `ðŸ§© ${p.piece}`} {readyPlayers.includes(p.name) && 'âœ…'}
          </li>
        ))}
      </ul>
      {allReady && <p style={{color:'green'}}>All players ready, starting...</p>}
    </div>
  );
};

export default Lobby;
