import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';

export default function DiceRoller() {
  const {
    player,
    currentPlayerId,
    diceRoll,
    socket
  } = useContext(GameContext);

  // Only show on your turn
  if (!player || player.socketId !== currentPlayerId) {
    return null;
  }

  const roll = () => {
    socket.emit('rollDice');
  };

  return (
    <div style={{
      position: 'absolute',
      top: '10%',
      left: '50%',
      transform: 'translateX(-50%)',
      background: '#fff',
      border: '2px solid #333',
      borderRadius: '12px',
      padding: '1rem',
      boxShadow: '0 4px 10px rgba(0,0,0,0.2)',
      zIndex: 1000,
      textAlign: 'center'
    }}>
      {/* Before roll: show button */}
      {!diceRoll && (
        <button onClick={roll}>Roll Dice</button>
      )}

      {/* After roll: show two dice faces */}
      {diceRoll && diceRoll.playerId === player.socketId && (
        <div style={{
          marginTop: '1rem',
          display: 'flex',
          justifyContent: 'center',
          gap: '10px'
        }}>
          <img
            src={`/dice/dice${diceRoll.die1}.png`}
            alt={`Die ${diceRoll.die1}`}
            width={50}
          />
          <img
            src={`/dice/dice${diceRoll.die2}.png`}
            alt={`Die ${diceRoll.die2}`}
            width={50}
          />
        </div>
      )}
    </div>
  );
}
