// src/components/DiceRoller.js
import React, { useContext, useState } from 'react';
import { GameContext } from '../context/GameContext';

const DiceRoller = () => {
  const {
    players,
    player,
    currentPlayerIndex,
    isMoving,
    setOriginalCount,
    setIsMoving
  } = useContext(GameContext);
  const [dice, setDice] = useState(null);

  if (!player || players[currentPlayerIndex]?.name !== player.name) return null;

  const roll = () => {
    if (isMoving) return;
    const d1 = Math.ceil(Math.random() * 6);
    const d2 = Math.ceil(Math.random() * 6);
    const total = d1 + d2;
    setDice([d1, d2]);
    setOriginalCount(total);
    setIsMoving(true);
  };

  return (
    <div style={{
      position: 'absolute',
      top: '10%',
      left: '50%',
      transform: 'translateX(-50%)',
      background: '#fff',
      padding: '1rem 2rem',
      border: '2px solid #333',
      borderRadius: 12,
      boxShadow: '0 4px 10px rgba(0,0,0,0.2)',
      textAlign: 'center',
      zIndex: 1000
    }}>
      <h3>Your Turn</h3>
      {!dice && <button onClick={roll}>Roll Dice</button>}
      {dice && (
        <div style={{ marginTop: 10, display: 'flex', justifyContent: 'center', gap: 10 }}>
          <img src={`/dice/dice${dice[0]}.png`} alt="" width={50} />
          <img src={`/dice/dice${dice[1]}.png`} alt="" width={50} />
        </div>
      )}
    </div>
  );
};

export default DiceRoller;
