// src/components/DiceRoller.js
import React, { useContext, useState } from 'react';
import { GameContext } from '../context/GameContext';

const DiceRoller = () => {
  const { player, players, currentPlayerIndex, setOriginalCount } = useContext(GameContext);
  const [diceRoll, setDiceRoll] = useState([null, null]);

  const isMyTurn = players[currentPlayerIndex]?.id === player.id;

  const rollDice = () => {
    const d1 = Math.ceil(Math.random() * 6);
    const d2 = Math.ceil(Math.random() * 6);
    setDiceRoll([d1, d2]);
    setOriginalCount(d1 + d2);
  };

  if (!isMyTurn) return null;

  return (
    <div style={{
      position: 'absolute',
      top: '20%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      backgroundColor: '#fff',
      padding: '20px',
      border: '2px solid #444',
      borderRadius: '10px',
      zIndex: 1000,
      textAlign: 'center'
    }}>
      <h2>Your Turn!</h2>
      <button onClick={rollDice}>ðŸŽ² Roll Dice</button>
      <div style={{ display: 'flex', justifyContent: 'center', marginTop: '10px' }}>
        {diceRoll.map((val, idx) =>
          val ? (
            <img
              key={idx}
              src={require(`../assets/dice/dice${val}.png`)}
              alt={`dice ${val}`}
              style={{ width: 79, height: 79, margin: '0 10px' }}
            />
          ) : null
        )}
      </div>
    </div>
  );
};

export default DiceRoller;
