import React, { createContext, useState, useEffect } from 'react';
import socket from '../socket';

export const GameContext = createContext();

export function GameProvider({ children }) {
  const [player, setPlayer] = useState(null);
  const [players, setPlayers] = useState([]);
  const [currentPlayerId, setCurrentPlayerId] = useState(null);
  const [sessionId, setSessionId] = useState(null);
  const [gameState, setGameState] = useState('lobby');

  // Utility: update "player" context when player list changes
  const updateLocalPlayer = (list) => {
    const me = list.find(p => p.socketId === socket.id);
    setPlayer(me || null);
  };

  useEffect(() => {
    // Log all events for debug
    socket.onAny((event, ...args) => {
      console.log("[SOCKET EVENT]", event, ...args);
    });

    const lobbyHandler = (newPlayers) => {
      setPlayers(newPlayers);
      updateLocalPlayer(newPlayers);
      console.log("[GameContext] (lobbyUpdate) New players:", newPlayers);
    };
    const gameStartHandler = (payload) => {
      console.log('[GameContext] *** GAMESTART EVENT RECEIVED ***', payload);
      setPlayers(payload.players);
      updateLocalPlayer(payload.players);
      setSessionId(payload.sessionId);
      setGameState('playing');
      setCurrentPlayerId(payload.currentPlayerId);
      console.log("[GameContext] gameStart - setCurrentPlayerId:", payload.currentPlayerId);
    };

    socket.on('lobbyUpdate', lobbyHandler);
    socket.on('gameStart', gameStartHandler);

    return () => {
      socket.off('lobbyUpdate', lobbyHandler);
      socket.off('gameStart', gameStartHandler);
      socket.offAny();
    };
  }, []);

  useEffect(() => {
    console.log('[GameContext.js Render]', {
      player,
      currentPlayerId,
      gameState,
      players,
      socketId: socket.id
    });
  }, [player, currentPlayerId, gameState, players]);

  return (
    <GameContext.Provider
      value={{
        player,
        setPlayer,
        players,
        setPlayers,
        currentPlayerId,
        setCurrentPlayerId,
        sessionId,
        setSessionId,
        socket,
        gameState,
        setGameState,
      }}
    >
      {children}
    </GameContext.Provider>
  );
}
