import React from 'react';
import bgImage from '../assets/bg.png';
import Board from './Board';
import DiceRoller from './DiceRoller';
import Dashboard from './Dashboard';
import PlayerStats from './PlayerStats';

const GameScreen = () => (
  <div
    style={{
      display: 'flex',
      flexDirection: 'column',
      minHeight: '100vh',
      position: 'relative',
      backgroundImage: `url(${bgImage})`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      backgroundRepeat: 'no-repeat',
      overflow: 'hidden',
    }}
  >
    {/* Floating top window divided into 4 sections */}
    <div
      style={{
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        display: 'flex',
        zIndex: 100,
        background: 'rgba(255, 255, 255, 0.9)',
        boxShadow: '0 2px 8px rgba(0,0,0,0.2)',
      }}
    >
      {/* 1st quarter with DiceRoller */}
      <div
        style={{
          flex: 1,
          borderRight: '1px solid #333',
          paddingTop: '1px solid #333',
          position: 'relative',
        }}
      >
        <DiceRoller />
      </div>
      {/* Empty 2nd quarter */}
      <div style={{ flex: 1, borderRight: '1px solid #333' }} />
      {/* Empty 3rd quarter */}
      <div style={{ flex: 1, borderRight: '1px solid #333' }} />
      {/* Empty 4th quarter */}
      <div style={{ flex: 1 }} />
    </div>

    {/* Main row: offset by floating window height */}
    <div
      style={{
        marginTop: '240px', // adjust if floating window height changes
        flex: 1,
        display: 'flex',
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center',
        position: 'relative',
      }}
    >
      {/* PlayerStats on the left */}
      <div
        style={{
          minWidth: 220,
          height: '80%',
          marginRight: 48,
          background: 'rgba(206, 206, 206, 0.96)',
          borderRight: '2px solid #d3d3d3',
          borderRadius: 12,
          boxShadow: '0 0 12px 0 #eee',
          padding: '16px 10px',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'stretch',
        }}
      >
        <h4 style={{ textAlign: 'center', margin: 0, marginBottom: 10 }}>
          Other Players
        </h4>
        <PlayerStats />
      </div>

      {/* Board in the center */}
      <div style={{ position: 'relative', zIndex: 2 }}>
        <Board />
      </div>
    </div>

    {/* Player's own dashboard at the bottom */}
    <div
      style={{
        width: '100%',
        background: '#fff',
        borderTop: '2px solid #bbb',
        boxShadow: '0 -2px 10px #ececec',
        padding: '0 0 18px 0',
        zIndex: 10,
      }}
    >
      <Dashboard />
    </div>
  </div>
);

export default GameScreen;
