import React, { useContext, useState } from 'react';
import { GameContext } from '../context/GameContext';

// require dice images under src/assets/dice/
const diceFaces = {
  1: require('../assets/dice/dice1.png'),
  2: require('../assets/dice/dice2.png'),
  3: require('../assets/dice/dice3.png'),
  4: require('../assets/dice/dice4.png'),
  5: require('../assets/dice/dice5.png'),
  6: require('../assets/dice/dice6.png'),
};

const DiceRoller = () => {
  const {
    player, players,
    currentPlayerIndex,
    setPlayers,
    setOriginalCount,
    isMoving,
    setCurrentPlayerIndex
  } = useContext(GameContext);
  const [diceRoll, setDiceRoll] = useState(null);

  // Only show on your turn
  if (!player || players[currentPlayerIndex]?.name !== player.name) {
    return null;
  }

  const rollDice = () => {
    if (isMoving || diceRoll) return;
    const d1 = Math.ceil(Math.random()*6);
    const d2 = Math.ceil(Math.random()*6);
    setDiceRoll([d1,d2]);
    const total = d1 + d2;
    setOriginalCount(total);

    setPlayers(prev => {
      const next = [...prev];
      next[currentPlayerIndex] = {
        ...next[currentPlayerIndex],
        prevTile: next[currentPlayerIndex].tileId,
        currentCount: total
      };
      return next;
    });
  };

  const handleDone = () => {
    setDiceRoll(null);
    setCurrentPlayerIndex(i => (i+1) % players.length);
  };

  return (
    <div style={{
      position:'absolute', top:'10%', left:'50%',
      transform:'translateX(-50%)', background:'#fff',
      border:'2px solid #333', borderRadius:12,
      padding:16, boxShadow:'0 4px 10px rgba(0,0,0,0.2)',
      zIndex:1000, textAlign:'center'
    }}>
      <h3>Your Turn</h3>
      {!diceRoll ? (
        <button onClick={rollDice}>Roll Dice</button>
      ) : (
        <>
          <div style={{ display:'flex', justifyContent:'center', margin:16 }}>
            <img src={diceFaces[diceRoll[0]]} alt="" width={50}/>
            <img src={diceFaces[diceRoll[1]]} alt="" width={50} style={{ marginLeft:10 }}/>
          </div>
          {!isMoving && <button onClick={handleDone}>Done</button>}
        </>
      )}
    </div>
  );
};

export default DiceRoller;
