// src/components/Board.js
import React, { useContext, useEffect, useRef } from 'react';
import { GameContext } from '../context/GameContext';
import boardImg from '../assets/board.png';
import { tiles } from '../data/tiles';

const Board = () => {
  const canvasRef = useRef(null);
  const { players } = useContext(GameContext);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const board = new Image();
    board.src = boardImg;

    const draw = () => {
      if (!canvas || !ctx) return;
      canvas.width = board.width;
      canvas.height = board.height;
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(board, 0, 0);

      players.forEach((player, i) => {
        const tile = tiles[player.tileId || 1]; // default to tile 1
        if (!tile || !player.piece) return;

        const img = new Image();
        img.src = require(`../assets/pieces/${player.piece}`);
        img.onload = () => {
          const size = 30 * 2.5; // 250% larger
          const offsetX = (i % 3) * 10;
          const offsetY = Math.floor(i / 3) * 10;
          const x = tile.x - size / 2 + offsetX;
          const y = tile.y - size / 2 + offsetY;
          ctx.drawImage(img, x, y, size, size);
        };
      });
    };

    board.onload = draw;
  }, [players]);

  return (
    <div style={{ display: 'flex', justifyContent: 'center', marginTop: '10px' }}>
      <canvas ref={canvasRef} style={{ border: '2px solid black' }} />
    </div>
  );
};

export default Board;
