import React, { useState, useContext } from 'react';
import { GameContext } from '../context/GameContext';
import { tiles } from '../data/tiles';

const TradePanel = () => {
  const { socket, player, players } = useContext(GameContext);
  
  // Offer section state
  const [offerMoney, setOfferMoney] = useState(0);
  const [selectedOfferProperties, setSelectedOfferProperties] = useState([]);
  const [isOfferExpanded, setIsOfferExpanded] = useState(false);
  const [offerReady, setOfferReady] = useState(false);
  
  // Ask section state
  const [askMoney, setAskMoney] = useState(0);
  const [selectedPlayer, setSelectedPlayer] = useState(null);
  const [selectedAskProperties, setSelectedAskProperties] = useState([]);
  const [isAskExpanded, setIsAskExpanded] = useState(false);
  const [isPropertiesExpanded, setIsPropertiesExpanded] = useState(false);
  const [askReady, setAskReady] = useState(false);

  // Incoming offers state
  const [incomingOffers, setIncomingOffers] = useState([]);

  // Effect to listen for trade requests
  React.useEffect(() => {
    if (!socket) return;

    socket.on('tradeRequest', (offer) => {
      setIncomingOffers(prev => [...prev, offer]);
    });

    socket.on('tradeAccepted', ({ offerId }) => {
      setIncomingOffers(prev => prev.filter(offer => offer.id !== offerId));
    });

    socket.on('tradeRejected', ({ offerId }) => {
      setIncomingOffers(prev => prev.filter(offer => offer.id !== offerId));
    });

    return () => {
      socket.off('tradeRequest');
      socket.off('tradeAccepted');
      socket.off('tradeRejected');
    };
  }, [socket]);

  // Update ready states
  React.useEffect(() => {
    setOfferReady(offerMoney >= 500 || selectedOfferProperties.length > 0);
  }, [offerMoney, selectedOfferProperties]);

  React.useEffect(() => {
    setAskReady(askMoney >= 500 || selectedAskProperties.length > 0);
  }, [askMoney, selectedAskProperties]);

  const handleMoneyChange = (section, amount) => {
    if (section === 'offer') {
      const newAmount = Math.max(0, Math.min(offerMoney + amount, player.money));
      setOfferMoney(newAmount);
    } else {
      const newAmount = Math.max(0, askMoney + amount);
      setAskMoney(newAmount);
    }
  };

  const handlePropertyToggle = (section, propertyId) => {
    if (section === 'offer') {
      setSelectedOfferProperties(prev => 
        prev.includes(propertyId) 
          ? prev.filter(id => id !== propertyId)
          : [...prev, propertyId]
      );
    } else {
      setSelectedAskProperties(prev => 
        prev.includes(propertyId) 
          ? prev.filter(id => id !== propertyId)
          : [...prev, propertyId]
      );
    }
  };

  const handlePlayerSelect = (playerId) => {
    setSelectedPlayer(playerId);
    setSelectedAskProperties([]); // Reset selected properties when player changes
    setIsPropertiesExpanded(false);
  };

  const handleTradeRequest = () => {
    if (!offerReady || !askReady || !selectedPlayer) return;

    const tradeRequest = {
      from: player.socketId,
      to: selectedPlayer,
      offer: {
        money: offerMoney,
        properties: selectedOfferProperties
      },
      ask: {
        money: askMoney,
        properties: selectedAskProperties
      },
      id: Date.now()
    };

    socket.emit('tradeRequest', tradeRequest);

    // Reset form
    setOfferMoney(0);
    setSelectedOfferProperties([]);
    setAskMoney(0);
    setSelectedAskProperties([]);
    setSelectedPlayer(null);
    setIsOfferExpanded(false);
    setIsAskExpanded(false);
  };

  const handleTradeResponse = (offerId, accepted) => {
    socket.emit('tradeResponse', { offerId, accepted });
  };

  const buttonStyle = {
    width: '100%',
    padding: '12px',
    border: 'none',
    borderRadius: '8px',
    color: 'white',
    fontSize: '1.1em',
    cursor: 'pointer',
    marginBottom: '10px',
    transition: 'background-color 0.2s'
  };

  const moneyControlStyle = {
    display: 'flex',
    alignItems: 'center',
    gap: '10px',
    marginBottom: '15px',
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
    padding: '10px',
    borderRadius: '8px'
  };

  const moneyButtonStyle = {
    padding: '8px 16px',
    backgroundColor: 'rgba(255, 255, 255, 0.2)',
    border: 'none',
    borderRadius: '4px',
    color: 'white',
    cursor: 'pointer',
    fontSize: '1.2em'
  };

  const propertyStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '8px',
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
    borderRadius: '4px',
    marginBottom: '5px'
  };

  return (
    <div style={{
      backgroundColor: 'rgba(0, 0, 0, 0.8)',
      borderRadius: '12px',
      color: 'white',
      padding: '20px',
      display: 'flex',
      flexDirection: 'column',
      gap: '20px'
    }}>
      <h3 style={{ margin: '0 0 15px 0', textAlign: 'center' }}>Trade Center</h3>

      {/* Offer Section */}
      <div>
        <button 
          onClick={() => setIsOfferExpanded(!isOfferExpanded)}
          style={{
            ...buttonStyle,
            backgroundColor: isOfferExpanded ? '#388E3C' : '#4CAF50'
          }}
        >
          Your Offer
        </button>
        
        {isOfferExpanded && (
          <div style={{ marginBottom: '20px' }}>
            {/* Money Input */}
            <div style={moneyControlStyle}>
              <button 
                onClick={() => handleMoneyChange('offer', -500)}
                style={moneyButtonStyle}
                disabled={offerMoney <= 0}
              >
                -
              </button>
              <span style={{ flex: 1, textAlign: 'center', fontSize: '1.2em' }}>
                ${offerMoney}
              </span>
              <button 
                onClick={() => handleMoneyChange('offer', 500)}
                style={moneyButtonStyle}
                disabled={offerMoney >= player.money}
              >
                +
              </button>
            </div>

            {/* Properties Selection */}
            <div style={{
              backgroundColor: 'rgba(255, 255, 255, 0.1)',
              padding: '15px',
              borderRadius: '8px'
            }}>
              <h4 style={{ margin: '0 0 10px 0' }}>Your Properties</h4>
              {player.properties.map(propId => {
                const property = tiles.find(t => t.id === propId);
                return (
                  <div key={propId} style={propertyStyle}>
                    <span>{property.name}</span>
                    <input
                      type="checkbox"
                      checked={selectedOfferProperties.includes(propId)}
                      onChange={() => handlePropertyToggle('offer', propId)}
                      style={{ transform: 'scale(1.2)' }}
                    />
                  </div>
                );
              })}
            </div>
          </div>
        )}
      </div>

      {/* Ask Section */}
      <div>
        <button 
          onClick={() => setIsAskExpanded(!isAskExpanded)}
          style={{
            ...buttonStyle,
            backgroundColor: isAskExpanded ? '#1976D2' : '#2196F3'
          }}
        >
          What You Want
        </button>
        
        {isAskExpanded && (
          <div style={{ marginBottom: '20px' }}>
            {/* Player Selection */}
            <div style={{
              marginBottom: '15px',
              backgroundColor: 'rgba(255, 255, 255, 0.1)',
              padding: '15px',
              borderRadius: '8px'
            }}>
              <h4 style={{ margin: '0 0 10px 0' }}>Select Player</h4>
              {players
                .filter(p => p.socketId !== player.socketId)
                .map(p => (
                  <button
                    key={p.socketId}
                    onClick={() => handlePlayerSelect(p.socketId)}
                    style={{
                      width: '100%',
                      padding: '8px',
                      marginBottom: '5px',
                      backgroundColor: selectedPlayer === p.socketId 
                        ? 'rgba(33, 150, 243, 0.5)' 
                        : 'rgba(255, 255, 255, 0.2)',
                      border: 'none',
                      borderRadius: '4px',
                      color: 'white',
                      cursor: 'pointer'
                    }}
                  >
                    {p.name}
                  </button>
                ))}
            </div>

            {selectedPlayer && (
              <>
                {/* Money Input */}
                <div style={moneyControlStyle}>
                  <button 
                    onClick={() => handleMoneyChange('ask', -500)}
                    style={moneyButtonStyle}
                    disabled={askMoney <= 0}
                  >
                    -
                  </button>
                  <span style={{ flex: 1, textAlign: 'center', fontSize: '1.2em' }}>
                    ${askMoney}
                  </span>
                  <button 
                    onClick={() => handleMoneyChange('ask', 500)}
                    style={moneyButtonStyle}
                  >
                    +
                  </button>
                </div>

                {/* Properties Selection */}
                <div style={{
                  backgroundColor: 'rgba(255, 255, 255, 0.1)',
                  padding: '15px',
                  borderRadius: '8px'
                }}>
                  <h4 style={{ margin: '0 0 10px 0' }}>Their Properties</h4>
                  {players
                    .find(p => p.socketId === selectedPlayer)
                    ?.properties.map(propId => {
                      const property = tiles.find(t => t.id === propId);
                      return (
                        <div key={propId} style={propertyStyle}>
                          <span>{property.name}</span>
                          <input
                            type="checkbox"
                            checked={selectedAskProperties.includes(propId)}
                            onChange={() => handlePropertyToggle('ask', propId)}
                            style={{ transform: 'scale(1.2)' }}
                          />
                        </div>
                      );
                    })}
                </div>
              </>
            )}
          </div>
        )}
      </div>

      {/* Trade Request Button */}
      <button
        onClick={handleTradeRequest}
        disabled={!offerReady || !askReady || !selectedPlayer}
        style={{
          ...buttonStyle,
          backgroundColor: offerReady && askReady && selectedPlayer 
            ? '#FF9800' 
            : 'rgba(255, 152, 0, 0.5)',
          cursor: offerReady && askReady && selectedPlayer ? 'pointer' : 'not-allowed'
        }}
      >
        Propose Trade
      </button>

      {/* Incoming Offers */}
      {incomingOffers.length > 0 && (
        <div style={{
          marginTop: '20px',
          backgroundColor: 'rgba(255, 255, 255, 0.1)',
          padding: '15px',
          borderRadius: '8px'
        }}>
          <h4 style={{ margin: '0 0 10px 0' }}>Incoming Offers</h4>
          {incomingOffers.map(offer => {
            const fromPlayer = players.find(p => p.socketId === offer.from);
            return (
              <div 
                key={offer.id}
                style={{
                  backgroundColor: 'rgba(0, 0, 0, 0.3)',
                  padding: '15px',
                  borderRadius: '8px',
                  marginBottom: '10px'
                }}
              >
                <p style={{ margin: '0 0 10px 0' }}>
                  From: {fromPlayer?.name}
                </p>
                <div style={{ marginBottom: '10px' }}>
                  <strong>Offering:</strong>
                  <ul style={{ margin: '5px 0', paddingLeft: '20px' }}>
                    {offer.offer.money > 0 && (
                      <li>${offer.offer.money}</li>
                    )}
                    {offer.offer.properties.map(propId => {
                      const property = tiles.find(t => t.id === propId);
                      return <li key={propId}>{property.name}</li>;
                    })}
                  </ul>
                </div>
                <div style={{ marginBottom: '15px' }}>
                  <strong>Asking for:</strong>
                  <ul style={{ margin: '5px 0', paddingLeft: '20px' }}>
                    {offer.ask.money > 0 && (
                      <li>${offer.ask.money}</li>
                    )}
                    {offer.ask.properties.map(propId => {
                      const property = tiles.find(t => t.id === propId);
                      return <li key={propId}>{property.name}</li>;
                    })}
                  </ul>
                </div>
                <div style={{
                  display: 'flex',
                  gap: '10px'
                }}>
                  <button
                    onClick={() => handleTradeResponse(offer.id, true)}
                    style={{
                      flex: 1,
                      padding: '8px',
                      backgroundColor: '#4CAF50',
                      border: 'none',
                      borderRadius: '4px',
                      color: 'white',
                      cursor: 'pointer'
                    }}
                  >
                    Accept
                  </button>
                  <button
                    onClick={() => handleTradeResponse(offer.id, false)}
                    style={{
                      flex: 1,
                      padding: '8px',
                      backgroundColor: '#f44336',
                      border: 'none',
                      borderRadius: '4px',
                      color: 'white',
                      cursor: 'pointer'
                    }}
                  >
                    Reject
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default TradePanel;
