import React, { useContext, useState, useEffect } from 'react';
import bgImage from '../assets/bg.png';
import Dicebox from '../assets/diceBoard.png';
import Board from './Board';
import DiceRoller from './DiceRoller';
import Dashboard from './Dashboard';
import PlayerStats from './PlayerStats';
import { GameContext } from '../context/GameContext';
import { tiles } from '../data/tiles';

export default function GameScreen() {
  const {
    player,
    players,
    setPlayer,
    setPlayers,
    currentPlayerId,
    socket,
    movementDone,
    rentNotification
  } = useContext(GameContext);

  const isMyTurn = player?.socketId === currentPlayerId;
  const [error, setError] = useState(null);

  // Determine metadata for the current tile
  const tileMeta = tiles.find(t => t.id === player?.tileId);

  // Debug logging to trace condition
  useEffect(() => {
    console.log('[GameScreen] Debug:', {
      isMyTurn,
      movementDone,
      tileMeta,
      "tileMeta.type": tileMeta?.type,
      "tileMeta.cost": tileMeta?.cost,
      "player.money": player?.money
    });
  }, [isMyTurn, movementDone, tileMeta, player]);

  // Listen for purchase events
  useEffect(() => {
    socket.on('purchaseSuccess', ({ socketId, money, properties }) => {
      console.log('[GameScreen] purchaseSuccess', { socketId, money, properties });
      const updated = players.map(p =>
        p.socketId === socketId ? { ...p, money, properties } : p
      );
      setPlayers(updated);
      if (player.socketId === socketId) {
        setPlayer({ ...player, money, properties });
      }
      setError(null);
    });

    socket.on('purchaseFailed', ({ reason }) => {
      console.log('[GameScreen] purchaseFailed', reason);
      if (reason === 'insufficientFunds') {
        setError("You don't have enough money.");
      } else if (reason === 'alreadyOwned') {
        setError('You already own this property.');
      } else if (reason === 'ownedByOther') {
        setError('This property is owned by another player.');
      } else {
        setError('Cannot buy this property.');
      }
    });

    return () => {
      socket.off('purchaseSuccess');
      socket.off('purchaseFailed');
    };
  }, [socket, player, players, setPlayer, setPlayers]);

  const handleBuy = () => {
    console.log('[GameScreen] handleBuy invoked');
    setError(null);
    socket.emit('buyProperty');
  };

  // Check if current property is owned by someone else
  const isPropertyOwned = () => {
    if (!tileMeta || tileMeta.type !== 'property') return false;
    return players.some(p => 
      p.properties && p.properties.includes(tileMeta.id.toString()) && p.socketId !== player?.socketId
    );
  };

  // Check if current property is owned by current player
  const isOwnProperty = () => {
    if (!tileMeta || tileMeta.type !== 'property') return false;
    return player?.properties && player.properties.includes(tileMeta.id.toString());
  };

  // Render rent notification
  const renderRentNotification = () => {
    if (!rentNotification) return null;

    const isPositive = rentNotification.type === 'ownProperty';
    const bgColor = isPositive ? 'rgba(34, 197, 94, 0.9)' : 'rgba(239, 68, 68, 0.9)';
    const textColor = 'white';

    return (
      <div
        style={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          background: bgColor,
          color: textColor,
          padding: '20px 30px',
          borderRadius: '12px',
          boxShadow: '0 4px 20px rgba(0,0,0,0.3)',
          zIndex: 1000,
          textAlign: 'center',
          fontSize: '18px',
          fontWeight: 'bold',
          minWidth: '300px',
          animation: 'fadeInOut 5s ease-in-out'
        }}
      >
        {rentNotification.type === 'ownProperty' ? (
          <>
            <div>üè† Property Bonus!</div>
            <div style={{ marginTop: '8px', fontSize: '16px' }}>
              {rentNotification.playerName} collected ${rentNotification.amount} from {rentNotification.propertyName}
            </div>
          </>
        ) : (
          <>
            <div>üí∞ Rent Paid!</div>
            <div style={{ marginTop: '8px', fontSize: '16px' }}>
              {rentNotification.payerName} paid ${rentNotification.amount} to {rentNotification.ownerName}
            </div>
            <div style={{ fontSize: '14px', opacity: 0.9 }}>
              for {rentNotification.propertyName}
            </div>
          </>
        )}
      </div>
    );
  };

  return (
    <div
      style={{
        display: 'flex',
        flexDirection: 'column',
        minHeight: '100vh',
        position: 'relative',
        backgroundImage: `url(${bgImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        overflow: 'hidden'
      }}
    >
      {/* Rent notification overlay */}
      {renderRentNotification()}

      {/* Top bar with 4 quarters */}
      <div
        style={{
          position: 'absolute',
          top: 0,
          left: '50%',
          transform: 'translateX(-50%)',
          width: '65%',
          height: '280px',
          display: 'flex',
          zIndex: 100,
          background: 'rgba(0, 0, 0, 0.74)',
          boxShadow: '0 2px 8px rgba(0,0,0,0.2)',
          flex: 1,
          borderRadius: '12px',
        }}
      >
        {/* 1st quarter: Dice board */}
        <div
          style={{
            flex: 1,
            position: 'relative',
            height: '100%',
            borderRight: '1px solid #333',
            overflow: 'hidden'
          }}
        >
          {isMyTurn && (
            <img
              src={Dicebox}
              alt="Dice Board"
              style={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                objectFit: 'fill',
                pointerEvents: 'none'
              }}
            />
          )}
          <DiceRoller />
        </div>

        {/* 2nd quarter: Buy Property Section */}
        <div
          style={{
            flex: 1,
            borderRight: '1px solid #333',
            height: '100%',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            padding: '16px'
          }}
        >
          {(() => {
            // Detailed debugging
            const isProperty = tileMeta?.type === 'property';
            const propertyOwned = isPropertyOwned();
            const ownProperty = isOwnProperty();
            const showBuyButton = isMyTurn && movementDone && isProperty && !propertyOwned && !ownProperty;

            console.log('[GameScreen] Detailed Buy button debug:', {
              isMyTurn,
              movementDone,
              isProperty,
              tileMeta,
              'tileMeta?.type': tileMeta?.type,
              propertyOwned,
              ownProperty,
              'player?.properties': player?.properties,
              'tileMeta.id': tileMeta?.id,
              showBuyButton
            });

            // Show property info if on a property
            if (isProperty) {
              return (
                <div style={{ textAlign: 'center', color: 'white' }}>
                  <h4 style={{ margin: '0 0 8px 0', fontSize: '16px' }}>
                    {tileMeta.name}
                  </h4>
                  <p style={{ margin: '0 0 8px 0', fontSize: '14px' }}>
                    Cost: ${tileMeta.cost} | Rent: ${tileMeta.rent}
                  </p>

                  {/* Debug info - remove this later */}
                  <div style={{ fontSize: '12px', opacity: 0.6, marginBottom: '8px' }}>
                    Debug: Turn:{isMyTurn?'Y':'N'} Move:{movementDone?'Y':'N'} Own:{ownProperty?'Y':'N'} Owned:{propertyOwned?'Y':'N'}
                  </div>

                  {ownProperty && (
                    <div style={{ color: '#22c55e', fontSize: '14px', fontWeight: 'bold' }}>
                      You own this property!
                    </div>
                  )}

                  {propertyOwned && (
                    <div style={{ color: '#ef4444', fontSize: '14px', fontWeight: 'bold' }}>
                      Owned by another player
                    </div>
                  )}

                  {showBuyButton && (
                    <>
                      <button
                        onClick={handleBuy}
                        disabled={player.money < tileMeta.cost}
                        style={{
                          padding: '12px 20px',
                          fontSize: '16px',
                          fontWeight: 'bold',
                          backgroundColor: player.money >= tileMeta.cost ? '#22c55e' : '#6b7280',
                          color: 'white',
                          border: 'none',
                          borderRadius: '8px',
                          cursor: player.money >= tileMeta.cost ? 'pointer' : 'not-allowed',
                          marginTop: '8px'
                        }}
                      >
                        Buy ${tileMeta.cost}
                      </button>

                      {error && (
                        <p style={{ 
                          color: '#ef4444', 
                          marginTop: '8px', 
                          fontSize: '14px',
                          fontWeight: 'bold'
                        }}>
                          {error}
                        </p>
                      )}
                    </>
                  )}
                </div>
              );
            }

            return (
              <div style={{ color: 'white', textAlign: 'center', fontSize: '14px' }}>
                {tileMeta ? `${tileMeta.name} (${tileMeta.type})` : 'No tile data'}
              </div>
            );
          })()}
        </div>

        {/* 3rd quarter: Current Tile Info */}
        <div 
          style={{ 
            flex: 1, 
            borderRight: '1px solid #333', 
            height: '100%',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            padding: '16px',
            color: 'white',
            textAlign: 'center'
          }}
        >
          {tileMeta && (
            <>
              <h4 style={{ margin: '0 0 8px 0', fontSize: '16px' }}>
                Current Tile
              </h4>
              <p style={{ margin: '0 0 4px 0', fontSize: '14px', fontWeight: 'bold' }}>
                {tileMeta.name}
              </p>
              <p style={{ margin: '0', fontSize: '12px', opacity: 0.8 }}>
                Type: {tileMeta.type}
              </p>
            </>
          )}
        </div>

        {/* 4th quarter: Game Status */}
        <div 
          style={{ 
            flex: 1, 
            height: '100%',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            padding: '16px',
            color: 'white',
            textAlign: 'center'
          }}
        >
          <h4 style={{ margin: '0 0 8px 0', fontSize: '16px' }}>
            {isMyTurn ? "Your Turn" : "Waiting..."}
          </h4>
          <p style={{ margin: '0', fontSize: '14px', opacity: 0.8 }}>
            {players.length} players in game
          </p>
        </div>
      </div>

      {/* Main content (board & dashboards) */}
      <div
        style={{
          marginTop: '240px',
          flex: 1,
          display: 'flex',
          flexDirection: 'row',
          alignItems: 'center',
          justifyContent: 'center',
          position: 'relative'
        }}
      >
        <div
          style={{
            minWidth: 220,
            height: '80%',
            marginRight: 48,
            background: 'rgba(206, 206, 206, 0.96)',
            borderRight: '2px solid #d3d3d3',
            borderRadius: 12,
            boxShadow: '0 0 12px 0 #eee',
            padding: '16px 10px',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'stretch'
          }}
        >
          <h4 style={{ textAlign: 'center', margin: 0, marginBottom: 10 }}>
            Other Players
          </h4>
          <PlayerStats />
        </div>

        <div style={{ position: 'relative', zIndex: 2 }}>
          <Board />
        </div>
      </div>

      {/* Bottom dashboard */}
      <div
        style={{
          width: '100%',
          background: 'rgba(0, 0, 0, 0.74)',
          borderTop: '2px solid #bbb',
          boxShadow: '0 -2px 10px #ececec',
          padding: '0 0 18px 0',
          zIndex: 10
        }}
      >
        <Dashboard />
      </div>

      <style jsx>{`
        @keyframes fadeInOut {
          0% { opacity: 0; transform: translate(-50%, -50%) scale(0.8); }
          10% { opacity: 1; transform: translate(-50%, -50%) scale(1); }
          90% { opacity: 1; transform: translate(-50%, -50%) scale(1); }
          100% { opacity: 0; transform: translate(-50%, -50%) scale(0.8); }
        }
      `}</style>
    </div>
  );
}
