// client/src/components/GameScreen.js
import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';

export default function GameScreen() {
  const { players, currentPlayerId, socket, sessionId } = useContext(GameContext);
  const me = socket.id;
  const isMyTurn = me === currentPlayerId;

  return (
    <div>
      <h2>Session: {sessionId}</h2>
      <div className="board">
        {players.map(p => (
          <div
            key={p.id}
            className="piece"
            style={{ /* position via p.tileId */ }}
          >
            {p.name}
          </div>
        ))}
      </div>

      {isMyTurn && (
        <button onClick={() => socket.emit('rollDice')}>
          Roll Dice
        </button>
      )}

      <button onClick={() => socket.emit('endTurn')}>
        End Turn
      </button>
    </div>
  );
}
