import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';
import io from 'socket.io-client';

const socket = io('http://localhost:3001');

const Dashboard = () => {
  const {
    player: localPlayer,
    players,
    currentPlayerIndex,
    setCurrentPlayerIndex,
    isMoving
  } = useContext(GameContext);

  if (!localPlayer) return null;

  const currentPlayer = players[currentPlayerIndex];
  // Find the local player’s up-to-date data in the array:
  const me = players.find(p => p.name === localPlayer.name) || localPlayer;
  const isMyTurn = currentPlayer && currentPlayer.name === me.name;

  const handleExit = () => {
    socket.emit('exitLobby');
  };

  const handleDone = () => {
    setCurrentPlayerIndex((idx) => (idx + 1) % players.length);
  };

  return (
    <div
      style={{
        position: 'absolute',
        bottom: 0,
        left: '50%',
        transform: 'translateX(-50%)',
        width: '80%',
        maxWidth: '700px',
        backgroundColor: '#fff',
        boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
        borderTop: '2px solid #ccc',
        padding: '1rem',
        textAlign: 'center',
        zIndex: 500
      }}
    >
      <h3 style={{ marginBottom: '1rem' }}>{me.name}’s Dashboard</h3>
      <p><strong>Money:</strong> ${me.money}</p>
      <p>
        <strong>Properties:</strong>{' '}
        {me.properties?.length ? me.properties.join(', ') : 'None'}
      </p>
      <div style={{ marginTop: '1rem' }}>
        <button onClick={handleExit}>Exit Game</button>
        {isMyTurn && !isMoving && (
          <button
            onClick={handleDone}
            style={{ marginLeft: '1rem' }}
          >
            Done
          </button>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
