// src/components/GameController.js
import { useContext, useEffect } from 'react';
import { GameContext } from '../context/GameContext';
import { tiles } from '../data/tiles';

const GameController = () => {
  const {
    players,
    setPlayers,
    currentPlayerIndex,
    diceRoll,
    setDiceRoll,
    setIsMoving,
  } = useContext(GameContext);

  useEffect(() => {
    if (!diceRoll) return;  // bail if no roll

    const movePlayer = async () => {
      const steps = diceRoll[0] + diceRoll[1] - (diceRoll[0] + diceRoll[1]) + (diceRoll[0] + diceRoll[1]);
      // Actually diceRoll is [d1,d2], so:
      let remaining = diceRoll[0] + diceRoll[1];

      console.log(
        `[GameController] Starting move: ${remaining} steps for ${
          players[currentPlayerIndex].name
        }`
      );
      setIsMoving(true);

      // Make a shallow copy to mutate
      const updatedPlayers = [...players];
      const player = updatedPlayers[currentPlayerIndex];

      while (remaining > 0) {
        const currentTile = tiles.find((t) => t.id === (player.tileId || 1));
        const prev = player.prevTile;
        const rollSum = diceRoll[0] + diceRoll[1];
        const rollType = rollSum === 7 ? 'any' : rollSum < 7 ? 'below' : 'above';

        // filter valid next steps
        const candidates = (currentTile.next || []).filter(
          (n) =>
            (n.from === prev || n.from == null) &&
            (rollType === 'any' || n.roll === rollType || n.roll === 'any')
        );

        // choose next
        let nextId;
        if (rollSum === 7 && candidates.length > 1) {
          const names = candidates.map(
            (c) => tiles.find((t) => t.id === c.to)?.name || `${c.to}`
          );
          console.log(
            `[GameController] Branch on 7: options = ${names.join(', ')}`
          );
          const pickFirst = window.confirm(
            `Choose path:\nOK → ${names[0]}\nCancel → ${names[1]}`
          );
          nextId = pickFirst ? candidates[0].to : candidates[1].to;
        } else {
          nextId = candidates[0]?.to;
        }

        if (!nextId) {
          console.warn(
            `[GameController] No valid path from tile ${currentTile.id}`
          );
          break;
        }

        player.prevTile = player.tileId;
        player.tileId = nextId;
        setPlayers([...updatedPlayers]);

        remaining -= 1;
        console.log(
          `[GameController] Moved to tile ${nextId}, ${remaining} steps left`
        );

        // pause so you can see animation
        // eslint-disable-next-line no-await-in-loop
        await new Promise((res) => setTimeout(res, 500));
      }

      console.log(
        `[GameController] Move complete for ${player.name}`
      );
      setIsMoving(false);
      setDiceRoll(null); // clear roll to allow next turn
    };

    movePlayer();
  }, [diceRoll, players, currentPlayerIndex, setPlayers, setIsMoving, setDiceRoll]);

  return null;
};

export default GameController;
